/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	chkAll.value    = 0;
	chkBrkf.value   = 0;
	chkLunch.value  = 0;
	chkDinn.value   = 0;
	chkSnck1.value  = 0;
	chkSnck2.value  = 0;
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e03100010-service:SearchRacerFood";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerFood=dsOutRacerFood ";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("TMS"), cboSRaceTms.Value);	
    fce_AddParam(Trim("FD_EXP_DT"), calFdExpDt.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) { 

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerFood       ) )
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerFood;
	sMandatoryColID   = "STND_YEAR||MBR_CD||TMS||FD_EXP_DT||RACER_NO";
	sMandatoryColName = "년도||경정장||회차||급식일||등록번호";
	oGrid             = grdRacerFood;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcSave(obj) {

    if ( !fcSaveCheck(obj) ) return;

	var sServiceName = "e03100010-service:SaveRacerFood";
    var sInDataSet   = "dsOutRacerFood=dsOutRacerFood:u ";
    var sOutDataSet  = "dsOutRacerFood=dsOutRacerFood ";
    fnc_DatasetPrint(dsOutRacerFood);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

