function dsOutRacerFood_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
    var fd_bas = 0;
    if (strColumnID == "SUM_AMT") return;

    //체크됐다면 금액 합산
    if ( obj.GetColumn(nRow, strColumnID) > 0 ){ 
        if (strColumnID == "BRKF_PRC") {
            if (obj.GetColumn(nRow, "BRKF_PRC") != obj.GetColumn(nRow, "BRKF_BAS") || obj.GetColumn(nRow, "BRKF_PRC") == 1) {
                fd_bas = obj.GetColumn(nRow, "BRKF_BAS");
                obj.SetColumn(nRow, "BRKF_PRC"  , fd_bas);  
            }
        }
        if (strColumnID == "LUNCH_PRC") {
            if (obj.GetColumn(nRow, "LUNCH_PRC") != obj.GetColumn(nRow, "LUNCH_BAS") || obj.GetColumn(nRow, "LUNCH_PRC") == 1)     {
                fd_bas = obj.GetColumn(nRow, "LUNCH_BAS");
                obj.SetColumn(nRow, "LUNCH_PRC"  , fd_bas);
            }
        }        
        if (strColumnID == "DINN_PRC") {
            if (obj.GetColumn(nRow, "DINN_PRC") != obj.GetColumn(nRow, "DINN_BAS") || obj.GetColumn(nRow, "DINN_PRC") == 1)    {
                fd_bas = obj.GetColumn(nRow, "DINN_BAS");
                obj.SetColumn(nRow, "DINN_PRC"  , fd_bas);
            }
        }
        if (strColumnID == "SNCK_PRC1") {
            if (obj.GetColumn(nRow, "SNCK_PRC1") != obj.GetColumn(nRow, "SNCK_BAS") || obj.GetColumn(nRow, "SNCK_PRC1") == 1)      {
                fd_bas = obj.GetColumn(nRow, "SNCK_BAS");
                obj.SetColumn(nRow, "SNCK_PRC1"  , fd_bas);
            }
        }
        if (strColumnID == "SNCK_PRC2") {
            if (obj.GetColumn(nRow, "SNCK_PRC2") != obj.GetColumn(nRow, "SNCK_BAS") || obj.GetColumn(nRow, "SNCK_PRC2") == 1)      {
                fd_bas = obj.GetColumn(nRow, "SNCK_BAS");
                obj.SetColumn(nRow, "SNCK_PRC2"  , fd_bas);
            }
        }
        obj.SetColumn(nRow, "SUM_AMT"  , toInteger(obj.GetColumn(nRow, "SUM_AMT")) + toInteger(fd_bas));
    }else if ( obj.GetColumn(nRow, strColumnID) == 0 ) { //체크 해지시, 금액 차감
        if (strColumnID == "BRKF_PRC") {
            if (obj.GetColumn(nRow, "BRKF_PRC") == obj.GetColumn(nRow, "BRKF_BAS") || obj.GetColumn(nRow, "BRKF_PRC") == 0)      {
                fd_bas = obj.GetColumn(nRow, "BRKF_BAS");
            }
        }
        if (strColumnID == "LUNCH_PRC") {
            if (obj.GetColumn(nRow, "LUNCH_PRC") == obj.GetColumn(nRow, "LUNCH_BAS") || obj.GetColumn(nRow, "LUNCH_PRC") == 0)     {
                fd_bas = obj.GetColumn(nRow, "LUNCH_BAS");
            }
        }        
        if (strColumnID == "DINN_PRC") {
            if (obj.GetColumn(nRow, "DINN_PRC") == obj.GetColumn(nRow, "DINN_BAS") || obj.GetColumn(nRow, "DINN_PRC") == 0)      {
                fd_bas = obj.GetColumn(nRow, "DINN_BAS");
            }
        }
        if (strColumnID == "SNCK_PRC1") {
            if (obj.GetColumn(nRow, "SNCK_PRC1") == obj.GetColumn(nRow, "SNCK_BAS") || obj.GetColumn(nRow, "SNCK_PRC1") == 0)      {
                fd_bas = obj.GetColumn(nRow, "SNCK_BAS");
            }
        }
        if (strColumnID == "SNCK_PRC2") {
            if (obj.GetColumn(nRow, "SNCK_PRC2") == obj.GetColumn(nRow, "SNCK_BAS") || obj.GetColumn(nRow, "SNCK_PRC2") == 0)      {
                fd_bas = obj.GetColumn(nRow, "SNCK_BAS");
            }
        }
        obj.SetColumn(nRow, "SUM_AMT"  , toInteger(obj.GetColumn(nRow, "SUM_AMT")) - toInteger(fd_bas)); 
    }
}

