/*-------------------------------------------------------------------------------------------------+
>>  전체선택 체크시
+-------------------------------------------------------------------------------------------------*/
function chkAll_OnClick(obj,strValue)
{
	grdMaster.Redraw = false;
    //전체선택 하면 모두 체크
	if (chkAll.value == 1){
        for ( var i = 0; i < dsOutRacerFood.rowcount; i++ ) {        
                if(dsOutRacerFood.getColumn(i, "BRKF_PRC") == 0)    dsOutRacerFood.setColumn(i, "BRKF_PRC", 1);
                if(dsOutRacerFood.getColumn(i, "LUNCH_PRC") == 0)   dsOutRacerFood.setColumn(i, "LUNCH_PRC", 1);
                if(dsOutRacerFood.getColumn(i, "DINN_PRC") == 0)    dsOutRacerFood.setColumn(i, "DINN_PRC", 1);
                if(dsOutRacerFood.getColumn(i, "SNCK_PRC1") == 0)    dsOutRacerFood.setColumn(i, "SNCK_PRC1", 1);
                if(dsOutRacerFood.getColumn(i, "SNCK_PRC2") == 0)    dsOutRacerFood.setColumn(i, "SNCK_PRC2", 1);
        }
    //전체선택 해지시 모든 체크 해지
    }else if(chkAll.value == 0){
        for ( var i = 0; i < dsOutRacerFood.rowcount; i++ ) {        
                if(dsOutRacerFood.getColumn(i, "BRKF_PRC") > 0)    dsOutRacerFood.setColumn(i, "BRKF_PRC", 0);
                if(dsOutRacerFood.getColumn(i, "LUNCH_PRC") > 0)   dsOutRacerFood.setColumn(i, "LUNCH_PRC", 0);
                if(dsOutRacerFood.getColumn(i, "DINN_PRC") > 0)    dsOutRacerFood.setColumn(i, "DINN_PRC", 0);
                if(dsOutRacerFood.getColumn(i, "SNCK_PRC1") > 0)    dsOutRacerFood.setColumn(i, "SNCK_PRC1", 0);
                if(dsOutRacerFood.getColumn(i, "SNCK_PRC2") > 0)    dsOutRacerFood.setColumn(i, "SNCK_PRC2", 0);
                dsOutRacerFood.setColumn(i, "SUM_AMT", 0);
        }
    }
    grdMaster.Redraw = true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조식/중식/석식/간식1/간식2 체크시
+-------------------------------------------------------------------------------------------------*/
function chkFd_OnClick(obj,strValue)
{
    var strColumn = "";
    if (obj.id == "chkBrkf")    strColumn   = "BRKF_PRC";
    if (obj.id == "chkLunch")   strColumn   = "LUNCH_PRC";
    if (obj.id == "chkDinn")    strColumn   = "DINN_PRC";
    if (obj.id == "chkSnck1")   strColumn   = "SNCK_PRC1";
    if (obj.id == "chkSnck2")   strColumn   = "SNCK_PRC2";

	grdMaster.Redraw = false;
    //전체선택 하면 모두 체크
	if (obj.value == 1){
        for ( var i = 0; i < dsOutRacerFood.rowcount; i++ ) {        
                if(dsOutRacerFood.getColumn(i, strColumn) == 0)    dsOutRacerFood.setColumn(i, strColumn, 1);
        }
    //전체선택 해지시 모든 체크 해지
    }else if(obj.value == 0){
        for ( var i = 0; i < dsOutRacerFood.rowcount; i++ ) {        
                if(dsOutRacerFood.getColumn(i, strColumn) > 0)    dsOutRacerFood.setColumn(i, strColumn, 0);
        }
    }
    grdMaster.Redraw = true;
}
