/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {

		cboSMbr.Index = 0;
        cboSMbr.Value = CURR_USER_MBR_CD;

		fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");

		if(CURR_STND_YEAR == cboSStndYear.Value)
		{
			cboMonth.Value = substr(CURR_DT,4,2);
		}
		else
		{
			cboMonth.Value = '12';
		}

		//조회조건 선택
		rdoSearchCond.value = "001";
		cboMonth.Enable = true;
		cboSRaceTms.Enable = false;


		fcSearchTmsMonth();
        //fce_GetRaceTms(cboSStndYear.Value,cboSMbr.Value,"","dsOutSRaceTms");
		return;
	}

// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }

        //cboSRaceTms_OnChanged();
		return;
	}	

	// 리포트리스트 조회시
	if ( sServiceName == "e03100010-service:searchDateperFood" ) {
		return;
	}

	if( sServiceName == "e03100010-service:searchTmsMonth")	{
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {

}

