{
	fcSearchTmsMonth();
}

/*-------------------------------------------------------------------------------------------------+
>>  일자별 급식내역
+-------------------------------------------------------------------------------------------------*/
function btnFdDay_OnClick(obj)
{
    if ( !fcSearchCheck(obj) ) return;

 	http.sync = true;
 	if(rdoSearchCond.value == '001') fcSearchFoodlist_Month();	//월별
	if(rdoSearchCond.value == '002') fcSearchFoodlist_Tms();	//회차별
	http.sync = false;

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e03109010_foodlist_date.jrf";               //레포트 파일명
	var sDataSet  = "foodlist_date=dsOutDateperFood_rpt";   //"UBI 쿼리명, MIP Dataset명"

	var sArg = " STND_YEAR="+cboSStndYear.Value;
        //sArg += " TMS1="+cboSRaceTms1.Value;
	    //sArg += " TMS2="+cboSRaceTms2.Value;
	    sArg = " MONTH="+cboMonth.Value;
        sArg += " IMG_URL="+logImgUrl;
	var sRptDrct    = 1;     //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 월 TMS 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchTmsMonth() {
	var sServiceName = "e03100010-service:searchTmsMonth";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutTmsMonth=dsOutTmsMonth";

    dsOutTmsMonth.ClearData();
	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("MONTH"), cboMonth.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 월별급식내역 정보 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchFoodlist_Month(obj) {
	var sServiceName = "e03100010-service:searchDateperFood";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDateperFood_rpt=dsOutDateperFood_rpt";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("MONTH"), cboMonth.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 회차별급식내역 정보 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchFoodlist_Tms(obj) {
	var sServiceName = "e03100010-service:searchDateperFoodTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDateperFood_rpt=dsOutDateperFood_rpt";

	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("TMS"), cboSRaceTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  선수급식명부
+-------------------------------------------------------------------------------------------------*/
function btnFdRacer_OnClick(obj)
{
 	http.sync = true;
    fcSearchFoodlist_Racer();
	http.sync = false;

	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e03109020_foodlist_racer.jrf";               //레포트 파일명
	var sDataSet  = "foodlist_racer=dsOutRacerperFood_rpt";   //"UBI 쿼리명, MIP Dataset명"

	var sArg = " IMG_URL="+logImgUrl;
    i = 1;
   for ( var j = dsOutTmsMonth.getColumn(0,"F_TMS"); j < parseInt(dsOutTmsMonth.getColumn(0,"L_TMS"))+1; j++ ) { 
        sArg += " VAL"+i+"="+j;
        i++;
    } 
	var sRptDrct    = 1;     //출력방향(0 : 수직, 1 : 수평)
	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);	
}

/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 선수급식명부 정보 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchFoodlist_Racer(obj) {
	var sServiceName = "e03100010-service:searchRacerperFood";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerperFood_rpt=dsOutRacerperFood_rpt";
	fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD"), cboSMbr.Value);
	fce_AddParam(Trim("MONTH"), cboMonth.Value);
	fce_AddParam(Trim("VAL1"), "");
	fce_AddParam(Trim("VAL2"), "");
	fce_AddParam(Trim("VAL3"), "");
	fce_AddParam(Trim("VAL4"), "");
	fce_AddParam(Trim("VAL5"), "");
    i = 1;
    for ( var j = dsOutTmsMonth.getColumn(0,"F_TMS"); j < parseInt(dsOutTmsMonth.getColumn(0,"L_TMS"))+1; j++ ) { 
        fce_AddParam(Trim("VAL"+i), j);
        i++;
    }    

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == '001') {
		cboMonth.Enable = true;
		cboSRaceTms.Enable = false;
	}
	if(strCode == '002') {
		cboMonth.Enable = false;
		cboSRaceTms.Enable = true;
	}
}
