function fcInit(obj) {
   dsOutOrgan.ClearData();
}
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }
    if(fnc_isNull(cboSDayOrd.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "일차");
            cboSDayOrd.SetFocus();
            return false;
    } 
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;
    //지정연습편성조회 
	var sServiceName = "e04010010-service:searchOrgan";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutOrgan=dsOutOrgan";
    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutOrgan)) {
	//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	if ( !fcCheckData() ) return false;

	return true;
}

function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var ServiceName = "e04010010-service:saveOrgan";
    var sInDataSet   = "dsOutOrgan=dsOutOrgan:u";
    var sOutDataSet  = "dsOutOrgan=dsOutOrgan";

 	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack"); 
}

function fcCheckData(obj)
{
	var iRowCnt = dsOutOrgan.GetRowCount();

	// 0일차일때 1,2라운드 지정연습이 3개이상 같은 경우 메시지 표출
	if (cboSDayOrd.Value == 0) {
		var iEqualCnt = 0;
		for ( var i = 0; i < iRowCnt; i++ ) {
			sRunTm  = trim(dsOutOrgan.GetColumn(i, "RUN_TM"));
			sRunTm0 = trim(dsOutOrgan.GetColumn(i, "RUN_TM_0"));
			sOSTTm  = trim(dsOutOrgan.GetColumn(i, "OST_TM"));
			sOSTTm0 = trim(dsOutOrgan.GetColumn(i, "OST_TM_0"));
			if( (sRunTm != "") && (sRunTm0 != "") ) {
				if ( IndexOf(sRunTm, '.') <= 0 ) {
					switch ( length(sRunTm) ) {
					case 0 :			
						sRunTm  = trim(dsOutOrgan.GetColumn(i, "RUN_TM"));
						break;
					case 1 :			
						sRunTm  = trim(dsOutOrgan.GetColumn(i, "RUN_TM"));
						break;
					case 2 :			
						sRunTm  = ParseFloat(trim(dsOutOrgan.GetColumn(i, "RUN_TM"))) / 10;
						break;
					case 3 :			
						sRunTm  = ParseFloat(trim(dsOutOrgan.GetColumn(i, "RUN_TM"))) / 100;
						break;
					}
				}

				if( sRunTm == sRunTm0 ) iEqualCnt++;
				if ( ParseFloat(Greatest(sRunTm, sRunTm0)) - ParseFloat(Least(sRunTm, sRunTm0)) >= 0.4 ) {
					return fnc_Message(L_COM_CNF_0000, "Y", dsOutOrgan.GetColumn(i, "RACE_NO") + "조 " + dsOutOrgan.GetColumn(i, "RACER_NM") + "선수기록이 0.4초 이상 차이가 납니다.\n 그래도 저장하시겠습니까?");
			}
			if( (sOSTTm != "") && (sOSTTm0 != "") ) {
				if ( IndexOf(sOSTTm, '.') <= 0 ) {
					switch ( length(sOSTTm) ) {
					case 0 :			
						sOSTTm  = trim(dsOutOrgan.GetColumn(i, "OST_TM"));
						break;
					case 1 :			
						sOSTTm  = trim(dsOutOrgan.GetColumn(i, "OST_TM"));
						break;
					case 2 :			
						sOSTTm  = ParseFloat(trim(dsOutOrgan.GetColumn(i, "OST_TM"))) / 10;
						break;
					case 3 :			
						sOSTTm  = ParseFloat(trim(dsOutOrgan.GetColumn(i, "OST_TM"))) / 100;
						break;
					}
				}
				if( sRunTm == sRunTm0 ) iEqualOSTCnt++;
			}
			}
		}

		if (iEqualCnt >= 3) {
			return fnc_Message(L_COM_CNF_0000, "Y", "동일한 지정연습자료가 있습니다. 그래도 저장하시겠습니까?");
		}
		if (iEqualOSTCnt >= 3) {
			return fnc_Message(L_COM_CNF_0000, "Y", "동일한 OST자료가 있습니다. 그래도 저장하시겠습니까?");
		}

		return true;

	} else {
	// 1,2일차일때  해당회차 모든 지정연습자료가 0.4초이상 차이나는경우 메시지 표출
		var sRunTm, sRunTm00, sRunTm0, sRunTm1, sRunTm2;
		var sDiff;
		for ( var i = 0; i < iRowCnt; i++ ) {
			sRunTm00 = trim(dsOutOrgan.GetColumn(i, "DAY0_RUN_TM_0"));
			sRunTm0 = trim(dsOutOrgan.GetColumn(i, "DAY0_RUN_TM"));
			sRunTm1 = trim(dsOutOrgan.GetColumn(i, "DAY1_RUN_TM"));
			sRunTm2 = trim(dsOutOrgan.GetColumn(i, "DAY2_RUN_TM"));
			if ( IndexOf(trim(dsOutOrgan.GetColumn(i, "RUN_TM")),'.') > 0 ) {
				sRunTm  = trim(dsOutOrgan.GetColumn(i, "RUN_TM"));
			} else {
				switch ( length(trim(dsOutOrgan.GetColumn(i, "RUN_TM"))) ) {
					case 0 :			
						sRunTm  = trim(dsOutOrgan.GetColumn(i, "RUN_TM"));
						break;
					case 1 :			
						sRunTm  = trim(dsOutOrgan.GetColumn(i, "RUN_TM"));
						break;
					case 2 :			
						sRunTm  = ParseFloat(trim(dsOutOrgan.GetColumn(i, "RUN_TM"))) / 10;
						break;
					case 3 :			
						sRunTm  = ParseFloat(trim(dsOutOrgan.GetColumn(i, "RUN_TM"))) / 100;
						break;
				}
			}
			if ( ParseFloat(Greatest(sRunTm, sRunTm00, sRunTm0, sRunTm1, sRunTm1))
			   - ParseFloat(Least(sRunTm, sRunTm00, sRunTm0, sRunTm1, sRunTm1)) >= 0.4 ) {
				return fnc_Message(L_COM_CNF_0000, "Y", dsOutOrgan.GetColumn(i, "RACE_NO") + "조 " + dsOutOrgan.GetColumn(i, "RACER_NM") + "선수기록이 0.4초 이상 차이가 납니다.\n 그래도 저장하시겠습니까?");
			}
		}

		return true;
	}
}

