//키엔터 항목이동
function grdOrgan_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{

//0회차이면
 if (  cboSDayOrd.Value == 0) {
    if ( nCell == 4) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 5;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(4);
		} else {	 
		     grdOrgan.SetCellPos(11);
		}

	} else if ( nCell == 5) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 6;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(5);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(4);
		}


	} else if ( nCell == 6) {
	   if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 7;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(6);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(5);
		}
	} else if ( nCell == 7) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 8;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(7);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(6);
		}

	} else if ( nCell == 8) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 9;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(8);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(7);
		}

	} else if ( nCell == 9) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 10;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(8);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(8);
		}


	} else if ( nCell == 10) {
	   if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 11;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(9);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(9);
		}
	} else if ( nCell == 11) {
	  dsOutOrgan.row = nRow + 1 ; 
	  grdOrgan.SetCellPos(10);
	}

	grdOrgan.MoveToNextCell();
	grdOrgan.SetFocus();

 } else {      	
	if ( nCell == 4) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 5;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(4);
		} else {	 
		     grdOrgan.SetCellPos(7);
		}

	} else if ( nCell == 5) {
	    if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 6;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(5);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(4);
		}


	} else if ( nCell == 6) {
	   if ( nRow == dsOutOrgan.lastrow ) {
			nCell = 7;
			dsOutOrgan.row = 0; 
			grdOrgan.SetCellPos(6);
		} else {	 
		    dsOutOrgan.row = nRow + 1 ;
			grdOrgan.SetCellPos(5);
		}
	} else if ( nCell == 7) {
	  dsOutOrgan.row = nRow + 1 ; 
	  grdOrgan.SetCellPos(6);

	}

	grdOrgan.MoveToNextCell();
	grdOrgan.SetFocus();
 }
}

//키엔터시 값전체 선택
function grdOrgan_OnEnterEdit(obj,nRow,nCell,nPivotIndex)
{
	grdOrgan.SetEditSel(0,-1);
}

function dsOutOrgan_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( (strColumnID == "RUN_TM_0" && trim(dsOutOrgan.GetColumn(nRow, "RUN_TM")) <> "" && varNewValue <> "" ) ||
	     (strColumnID == "RUN_TM" && trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0")) <> "" && varNewValue <> "" ) ) {
			if ( IndexOf(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0")), '.') <= 0 ) {
				if ( length(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0"))) == 2)
					dsOutOrgan.SetColumn(nRow, "RUN_TM_0", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0"))) / 10));
				else if ( length(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0"))) == 3)
					dsOutOrgan.SetColumn(nRow, "RUN_TM_0", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0"))) / 100));
			} else {
				if(length(dsOutOrgan.GetColumn(nRow, "RUN_TM_0")) < 4) dsOutOrgan.SetColumn(nRow, "RUN_TM_0", trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0"))+'0');
				else dsOutOrgan.SetColumn(nRow, "RUN_TM_0", trim(dsOutOrgan.GetColumn(nRow, "RUN_TM_0")));
			}

			if ( IndexOf(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM")), '.') <= 0 ) {
				if ( length(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM"))) == 2)
					dsOutOrgan.SetColumn(nRow, "RUN_TM", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM"))) / 10));
				else if ( length(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM"))) == 3)
					dsOutOrgan.SetColumn(nRow, "RUN_TM", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "RUN_TM"))) / 100));
			} else {
				if(length(dsOutOrgan.GetColumn(nRow, "RUN_TM")) < 4) dsOutOrgan.SetColumn(nRow, "RUN_TM", trim(dsOutOrgan.GetColumn(nRow, "RUN_TM"))+'0');
				else dsOutOrgan.SetColumn(nRow, "RUN_TM", trim(dsOutOrgan.GetColumn(nRow, "RUN_TM")));
			}
	}
	if ( (strColumnID == "OST_TM_0" && trim(dsOutOrgan.GetColumn(nRow, "OST_TM")) <> "" && varNewValue <> "" ) ||
	     (strColumnID == "OST_TM" && trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0")) <> "" && varNewValue <> "" ) ) {
			if ( IndexOf(trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0")), '.') <= 0 ) {
				if ( length(trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0"))) == 2)
					dsOutOrgan.SetColumn(nRow, "OST_TM_0", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0"))) / 10));
				else if ( length(trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0"))) == 3)
					dsOutOrgan.SetColumn(nRow, "OST_TM_0", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0"))) / 100));
			} else {
				if(length(dsOutOrgan.GetColumn(nRow, "OST_TM_0")) < 4) dsOutOrgan.SetColumn(nRow, "OST_TM_0", trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0"))+'0');
				else dsOutOrgan.SetColumn(nRow, "OST_TM_0", trim(dsOutOrgan.GetColumn(nRow, "OST_TM_0")));
			}

			if ( IndexOf(trim(dsOutOrgan.GetColumn(nRow, "OST_TM")), '.') <= 0 ) {
				if ( length(trim(dsOutOrgan.GetColumn(nRow, "OST_TM"))) == 2)
					dsOutOrgan.SetColumn(nRow, "OST_TM", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "OST_TM"))) / 10));
				else if ( length(trim(dsOutOrgan.GetColumn(nRow, "OST_TM"))) == 3)
					dsOutOrgan.SetColumn(nRow, "OST_TM", toString(ParseFloat(trim(dsOutOrgan.GetColumn(nRow, "OST_TM"))) / 100));
			} else {
				if(length(dsOutOrgan.GetColumn(nRow, "OST_TM")) < 4) dsOutOrgan.SetColumn(nRow, "OST_TM", trim(dsOutOrgan.GetColumn(nRow, "OST_TM"))+'0');
				else dsOutOrgan.SetColumn(nRow, "OST_TM", trim(dsOutOrgan.GetColumn(nRow, "OST_TM")));
			}
	}
}
