/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

   //변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRace)) {
   //저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}
    // 필수항목체크
	oDataSet          = dsOutRace;
	sMandatoryColID   = "STND_YEAR|MBR_CD|TMS|DAY_ORD|RACE_NO|RACE_STTS_CD|STRT_TIME";
	sMandatoryColName = "기준년도|경정장|회차|일차|경주번호|경주상황|출발시각";
	oGrid             = "";
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

    //경주결과
	if( dsOutRace.GetRowCount() > 0 ) {
		for ( i = 0; i < dsOutVoilAct.GetRowCount(); i++ ) {         
			dsOutRace.SetColumn(i, "STND_YEAR", cboSStndYear.Value	); 
			dsOutRace.SetColumn(i, "MBR_CD"   , cboSMbr.Value 		); 
			dsOutRace.SetColumn(i, "TMS"      , cboSRaceTms.Value 	); 
			dsOutRace.SetColumn(i, "DAY_ORD"  , cboSDayOrd.Value	); 
			dsOutRace.SetColumn(i, "RACE_NO"  , cboSRaceNo.Value 	); 
		} 
	} 	

   return true;
}

function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var ServiceName = "e04020010-service:saveOrgan";
    var sInDataSet   = "dsOutRace=dsOutRace:u";
    var sOutDataSet  = "dsOutRace=dsOutRace";

 	fce_SendTransaction(ServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

