/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;
	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        dsOutRaceStts2Cd.InsertRow(0); //경주상황2 빈로우 생성
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
		return;
	}
	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
		dsOutSMaxTms.Filter("STND_YEAR='"+cboSStndYear.Value+"'&&MBR_CD='"+cboSMbr.Value+"'");
		var maxTms = dsOutSMaxTms.GetColumn(0,"MAX_TMS");
		dsOutSRaceTms.Filter("TMS<=" + maxTms);

        cboSRaceTms.Index = 0;
        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
        }
        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
        return;
	}
	// 일차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSDayOrd.Index = 0;

        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") 
         && cboSRaceTms.Value == fce_GetCurRaceInfo("TMS") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", fce_GetCurRaceInfo("DAY_ORD"));
            if ( nIndexRow > -1 ) cboSDayOrd.Index = nIndexRow;

        }

        //fcSearchRaceDayOrd();
        //-> 경주일자셋팅
        //->dsOutSRaceDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", cboSDayOrd.Value);
		dsOutSDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", cboSDayOrd.Value);

        fce_GetRace(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, cboSDayOrd.Value, "dsOutSRace");
		if(cboSStndYear.Value == "" || cboSStndYear.Value == null) 	return ;
		if(cboSMbr.Value 	  == "" || cboSMbr.Value == null) 		return ;
		if(cboSRaceTms.Value  == "" || cboSRaceTms.Value == null) 	return ;
		if(cboSDayOrd.Value   == "" || cboSDayOrd.Value == null) 	return ;
		if(cboSRaceNo.Value   == "" || cboSRaceNo.Value  == null) 	return ;
	    fcSearch();
		return;
	}
     //경주결과확정인조회 
	if ( sServiceName == "e04020010-service:searchUser" ) {
       return;
	}
	//경주결과조회후
	if ( sServiceName == "e04020010-service:searchRace" ) {

       if(dsOutRace.getColumn(0, "MNG") == "" || dsOutRace.getColumn(0, "MNG") == null)  {

			//경주결과확정인조회
			http.sync = true;
			var sServiceName = "e04020010-service:searchUser";
			var sInDataSet   = "";
			var sOutDataSet  = "dsOutUser=dsOutUser";
			dsOutUser.ClearData();
			fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
            http.sync = false;

            //담당자
			dsOutRace.SetColumn(0, "MNG"      			, dsOutUser.getColumn(0, "MNG")					); 
			dsOutRace.SetColumn(0, "MNG_NM"   			, dsOutUser.getColumn(0, "MNG_NM")				); 
			dsOutRace.SetColumn(0, "MNG_FILE_URL"   	, dsOutUser.getColumn(0, "MNG_FILE_URL")		); 

			//심판장
			dsOutRace.SetColumn(0, "REFE_HEAD"      	, dsOutUser.getColumn(0, "REFE_HEAD")			); 
			dsOutRace.SetColumn(0, "REFE_HEAD_NM"   	, dsOutUser.getColumn(0, "REFE_HEAD_NM")		);
			dsOutRace.SetColumn(0, "REFE_HEAD_FILE_URL" , dsOutUser.getColumn(0, "REFE_HEAD_FILE_URL")	); 

			//심사위원
			dsOutRace.SetColumn(0, "INSP_CMAN"      	, dsOutUser.getColumn(0, "INSP_CMAN")			); 
			dsOutRace.SetColumn(0, "INSP_CMAN_NM"   	, dsOutUser.getColumn(0, "INSP_CMAN_NM")		); 
			dsOutRace.SetColumn(0, "INSP_CMAN_FILE_URL" , dsOutUser.getColumn(0, "INSP_CMAN_FILE_URL")	); 

        }
        //경주상황 성립으로 기본설정
        if(dsOutRace.getColumn(0, "RACE_STTS_CD") == "" || dsOutRace.getColumn(0, "RACE_STTS_CD") == null)  {
           dsOutRace.SetColumn(0, "RACE_STTS_CD", "001"	); 
        }
        //기상 맑음으로 기본설정
        if(dsOutRace.getColumn(0, "WEATH_CD") == "" || dsOutRace.getColumn(0, "WEATH_CD") == null)  {
           dsOutRace.SetColumn(0, "WEATH_CD", "001"	); 
        }
        //확정여부
         if(dsOutRace.getColumn(0, "RACE_END_YN") == "" || dsOutRace.getColumn(0, "RACE_END_YN") == null)  {
            stRaceEndYn.Text = "미확정";
         } else {
            stRaceEndYn.Text = "확정";
         }
        return;
	}
    //경주결과조회(편성)
	if ( sServiceName == "e04020010-service:searchOrgan" ) {
		setLocation();
        return;
	}
    //경주결과조회(위반)
	if ( sServiceName == "e04020010-service:searchVoilAct" ) {
         return;
	}
	//경주결과조회(사고)
	if ( sServiceName == "e04020010-service:searchAcdntStts" ) {
       return;
	}
	//경주결과조회 (첨부파일)

    //경주결과조회(배당률)
	if ( sServiceName == "e04020010-service:searchDecision" ) {
	     return;
	}
     //경주기준정보조회
	if ( sServiceName == "e04020010-service:searchStnd" ) {
        return;
	}

	//편성=경주회차 및 경주일차 상태조회
	if ( sServiceName == "e04020010-service:searchTmsDayStat" ) {
      if(sInpt != "F"){   
			if( dsOutTmsDayStat.GetRowCount() > 0 ) {

			  //경주회차상태코드
			  if(dsOutTmsDayStat.getColumn(0, "RACE_TMS_STAT_CD") == "040" || dsOutTmsDayStat.getColumn(0, "RACE_TMS_STAT_CD") == "050")  {
				//경주일차상태코드
				if(dsOutTmsDayStat.getColumn(0, "ORGAN_STAT_CD") == "002")  {
				   btnSave.Visible 			= true;
				   //cmdRaceResult.Visible   	= true;
				   cmdRaceEnd.Visible      	= true;
				   bEnableTF                = "T";
				} else {
				   btnSave.Visible 			= false;
				   //cmdRaceResult.Visible   	= false;
				   cmdRaceEnd.Visible      	= false;
				   bEnableTF                = "F";
				}
			  } else {
				   btnSave.Visible 			= false;
				   //cmdRaceResult.Visible   	= false;
				   cmdRaceEnd.Visible     	= false;
				   bEnableTF                = "F";
			  }
			} else {
				   btnSave.Visible 			= false;
				   //cmdRaceResult.Visible   	= false;
				   cmdRaceEnd.Visible     	= false;
				   bEnableTF                = "F";
			}
	   }		
        return;
	}

    // 저장 후
	if ( sServiceName == "e04020010-service:saveOrgan" ) {
        fcSearch();
		return;
	}

	// 경주결과 확정후
	if ( sServiceName == "e04020010-service:saveRaceEnd" ) {
        fcSearch();
		return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

