	var nDay = GetDay( calFdExpDt.Value );
	var strDay = Decode( nDay, 0, "일", 1, "월", 2, "화", 
		3, "수", 4, "목", 5, "금", 6, "토" );

	var sArg  = " STND_YEAR="+cboSStndYear.Value; 
		sArg += " MBR_CD="+cboSMbr.Value;
        sArg += " TMS="+cboSRaceTms.Value;
        sArg += " DAY_ORD="+cboSDayOrd.Value;
        sArg += " RACE_DAY="+calFdExpDt.Value;
        sArg += " DAY_WEEK="+strDay+"요일";
		sArg += " IMG_URL="+logImgUrl; 

	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}

//위반내역출력
function btnDsOutVoilActReport_OnClick(obj)
{
	if ( !fcSearchCheck(obj) ) return;
	if( dsOutRace.GetRowCount() <= 0 ) {
	   fnc_Message(L_COM_ALT_0024, "N", "조회된 결과가 없으므로 처리할수");
		return false;
	}
	var sServiceName = "e04020010-service:searchVoil";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoil=dsOutVoil";
    dsOutVoil.ClearData();

    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
	fce_AddParam(Trim("RACE_NO  "), cboSRaceNo.Value  );

    http.sync= true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack",false);
    http.sync= false;

    //심판장 사진경로 가져오기
    var refeHeadUrl = split(dsOutRace.getColumn(0, "REFE_HEAD_FILE_URL"), "//");   //심판장
    var refeHeadFileName 	= GBL_MNGLINK_URL + refeHeadUrl[length(refeHeadUrl) - 1];




    var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     		//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e04020010_racer_voil.jrf";     //레포트 파일명
	var sDataSet  = "dsOutVoilReport=dsOutVoil";    //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "IMG_URL="+logImgUrl; 
		sArg 	+= " IMG_URL_REFE_HEAD="+refeHeadFileName;
		sArg 	+= " RaceEndYn="+stRaceEndYn.Text;

	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	 fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
