/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setLocation(){

	var oriBottom = grdOrgan.Bottom;
	var extHeight = 0;
	var rowHeight = 19;
	var rowCnt = dsOutOrgan.GetRowCount();
	var gap = 3;


	if(rowCnt < 6) rowCnt = 6;

	grdOrgan.Height = (rowCnt + 1) * rowHeight;

	grdRace.Top = grdOrgan.Bottom + gap;
	grdMan.Top = grdRace.Bottom + gap;
	grdRate.Top = grdMan.Bottom + gap;
	grdViewFile.Top = grdRate.Bottom + gap;

	grdVoilAct.Top = grdOrgan.Bottom + gap;
	grdAcdntStts.Top = grdVoilAct.Bottom + gap;
}

/*-------------------------------------------------------------------------------------------------+
>>  최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function fcSearchRsltCnt(){
	return 1;
	// 현업요청으로 체크 루틴 제거(2014.5.27)

	var sServiceName = "e04020010-service:searchRsltCnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsRsltCnt=dsRsltCnt";
    dsOutVoil.ClearData();

    fce_AddParam(Trim("STND_YEAR"), cboSStndYear.Value);
	fce_AddParam(Trim("TMS      "), cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), cboSDayOrd.Value  );
	fce_AddParam(Trim("RACE_NO  "), cboSRaceNo.Value  );

    http.sync= true;
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    http.sync= false;

    var nRsltCnt = dsRsltCnt.rowcount();

    return nRsltCnt;
}
