>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {

    var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e04030010_racer_exerc_organ.jrf";               //레포트 파일명
	var sDataSet  = "dsOutRaceReport=dsOutRace";    //"UBI 쿼리명, MIP Dataset명"

    //fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
    //var raceDayFrom = dsOutSDayOrd.getColumn(0, "RACE_DAY");
    var raceDayFrom = dsOutSDayOrd.getColumn(1, "RACE_DAY");
    var raceDayTo = dsOutSDayOrd.getColumn(2, "RACE_DAY");

    var sArg  = " TMS="+cboSRaceTms.Value; 
		sArg += " RACE_DAY_FROM="+raceDayFrom; 	
		sArg += " RACE_DAY_TO="+raceDayTo; 	
		sArg += " IMG_URL="+logImgUrl; 
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);


}
