/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;
	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
		return;
	}
	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;

        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms.Index = nIndexRow;
         }

        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
		return;
	}
	// 일차 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        cboSDayOrd.Index = 0;

        if ( cboSRaceTms.Value == CURR_TMS ) {
            var nIndexRow = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", CURR_DAY_ORD);

            if ( nIndexRow > -1 ) cboSDayOrd.Index = nIndexRow;
        }

        var befordDate = fnc_calDate(dsOutSDayOrd.GetColumn("0","RACE_DAY"),1,"Y");
        dsOutSDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", cboSDayOrd.Value);

        //코드값 추가, 경기전일자 날짜 세팅---------------------------------------------------//
        var nRow = 0;
        bFirst = "";

        if ( !bFirst ) {
            dsOutSDayOrd.InsertRow(nRow);
        } else {
            nRow = dsOutSDayOrd.GetRowCount();
            dsOutSDayOrd.AddRow();
        }

        dsOutSDayOrd.SetColumn(nRow, "DAY_ORD" , "0");
        dsOutSDayOrd.SetColumn(nRow, "RACE_DAY" , befordDate);

        if ( cboSDayOrd != null ) 
            cboSDayOrd.index = nRow;

            fcSearch();
            return;
	}


	//경주결과조회후
	if ( sServiceName == "e04030010-service:searchRace" ) {
         fnc_SetStatus(SNIS_BOA_0003, dsOutRace.GetRowCount());
         grdRace.Redraw = false;
         //평균구함
		if( dsOutRace.GetRowCount() > 0 ) {
			for ( i = 0; i < dsOutRace.GetRowCount(); i++ ) {         
				var j        = 0;
				var iMaxVal  = 0;
				var iMinVal  = 0;
				//if(raceRegNo == "" || raceRegNo == null) {
				//}
				var runTm_0 	  = dsOutRace.GetColumn(i, "RUN_TM_0" );        //0일차 지정연습
				if ( !fnc_IsNull(runTm_0) ) j++;
				  if ( !fnc_IsNull(runTm_0) ) {
					 iMaxVal = runTm_0;
					 iMinVal = runTm_0;
				  }

				var runTm_1 	  = dsOutRace.GetColumn(i, "RUN_TM_1" ); 	   //1일차 지정연습
				if ( !fnc_IsNull(runTm_1) ) j++;

				if ( !fnc_IsNull(runTm_0) ) {                                 //0일차 지정연습 미입력시 처리
					if ( !fnc_IsNull(runTm_1) ) {
						if (ToFloat(iMaxVal) > ToFloat(runTm_1)) {
							iMaxVal = iMaxVal;
						} else {
							iMaxVal = runTm_1;
						}

						if (ToFloat(iMinVal) > ToFloat(runTm_1)) {
							iMinVal = runTm_1;
						} else {
							iMinVal = iMinVal;
						}
					}
				} else {
				    iMaxVal = runTm_1;
					iMinVal = runTm_1;

				}

				var introRunTm1_1 = dsOutRace.GetColumn(i, "INTRO_RUN_TM_1_1" );//1일차 소개항주1
				if ( !fnc_IsNull(introRunTm1_1) ) j++;

				if ( !fnc_IsNull(introRunTm1_1) ) {
					if (ToFloat(iMaxVal) > ToFloat(introRunTm1_1)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = introRunTm1_1;
					}

					if (ToFloat(iMinVal) > ToFloat(introRunTm1_1)) {
						iMinVal = introRunTm1_1;
					} else {
						iMinVal = iMinVal;
					}
				}


				var introRunTm2_1 = dsOutRace.GetColumn(i, "INTRO_RUN_TM_2_1" );//1일차 소개항주2
				if ( !fnc_IsNull(introRunTm2_1) ) j++;

				if ( !fnc_IsNull(introRunTm2_1) ) {
					if (ToFloat(iMaxVal) > ToFloat(introRunTm2_1)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = introRunTm2_1;
					}

					if (ToFloat(iMinVal) > ToFloat(introRunTm2_1)) {
						iMinVal = introRunTm2_1;
					} else {
						iMinVal = iMinVal;
					}
				}

				var runTm_2 	  =	dsOutRace.GetColumn(i, "RUN_TM_2" ); 	   //2일차 지정연습
				if ( !fnc_IsNull(runTm_2) ) j++;

				if ( !fnc_IsNull(runTm_2) ) {
					if (ToFloat(iMaxVal) > ToFloat(runTm_2)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = runTm_2;
					}

					if (ToFloat(iMinVal) > ToFloat(runTm_2)) {
						iMinVal = runTm_2;
					} else {
						iMinVal = iMinVal;
					}
				}

				var introRunTm1_2 = dsOutRace.GetColumn(i, "INTRO_RUN_TM_1_2" );//2일차 소개항주1
				if ( !fnc_IsNull(introRunTm1_2) ) j++;

				if ( !fnc_IsNull(introRunTm1_2) ) {
					if (ToFloat(iMaxVal) > ToFloat(introRunTm1_2)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = introRunTm1_2;
					}

					if (ToFloat(iMinVal) > ToFloat(introRunTm1_2)) {
						iMinVal = introRunTm1_2;
					} else {
						iMinVal = iMinVal;
					}
				}


				var introRunTm2_2 =	dsOutRace.GetColumn(i, "INTRO_RUN_TM_2_2" );//2일차 소개항주2
				if ( !fnc_IsNull(introRunTm2_2) ) j++;

				if ( !fnc_IsNull(introRunTm2_2) ) {
					if (ToFloat(iMaxVal) > ToFloat(introRunTm2_2)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = introRunTm2_2;
					}

					if (ToFloat(iMinVal) > ToFloat(introRunTm2_2)) {
						iMinVal = introRunTm2_2;
					} else {
						iMinVal = iMinVal;
					}
				}


				var runTm_3 	  =	dsOutRace.GetColumn(i, "RUN_TM_3" ); 	   //3일차 지정연습
				if ( !fnc_IsNull(runTm_3) ) j++;

				if ( !fnc_IsNull(runTm_3) ) {
					if (ToFloat(iMaxVal) > ToFloat(runTm_3)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = runTm_3;
					}

					if (ToFloat(iMinVal) > ToFloat(runTm_3)) {
						iMinVal = runTm_3;
					} else {
						iMinVal = iMinVal;
					}
				}

				var introRunTm1_3 = dsOutRace.GetColumn(i, "INTRO_RUN_TM_1_3" );//3일차 소개항주1
				if ( !fnc_IsNull(introRunTm1_3) ) j++;

				if ( !fnc_IsNull(introRunTm1_3) ) {
					if (ToFloat(iMaxVal) > ToFloat(introRunTm1_3)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = introRunTm1_3;
					}

					if (ToFloat(iMinVal) > ToFloat(introRunTm1_3)) {
						iMinVal = introRunTm1_3;
					} else {
						iMinVal = iMinVal;
					}
				}

				var introRunTm2_3 =	dsOutRace.GetColumn(i, "INTRO_RUN_TM_2_3" );//3일차 소개항주2
				if ( !fnc_IsNull(introRunTm2_3) ) j++;

				if ( !fnc_IsNull(introRunTm2_3) ) {
					if (ToFloat(iMaxVal) > ToFloat(introRunTm2_3)) {
						iMaxVal = iMaxVal;
					} else {
						iMaxVal = introRunTm2_3;
					}

					if (ToFloat(iMinVal) > ToFloat(introRunTm2_3)) {
						iMinVal = introRunTm2_3;
					} else {
						iMinVal = iMinVal;
					}
				}


				var totAvg =( ToFloat(runTm_0)  
				           + ToFloat(runTm_1) + ToFloat(introRunTm1_1) + ToFloat(introRunTm2_1) 
				           + ToFloat(runTm_2) + ToFloat(introRunTm1_2) + ToFloat(introRunTm2_2) 
				           + ToFloat(runTm_3) + ToFloat(introRunTm1_3) + ToFloat(introRunTm2_3)) / ToInteger(j);


				var totStdDev = ToFloat(iMaxVal) - ToFloat(iMinVal);

				dsOutRace.SetColumn(i, "TOT_AVG", 			Round(totAvg	, 2) ); //평균
				dsOutRace.SetColumn(i, "TOT_STDDEV",    	Round(totStdDev , 2) ); //표준편차
			} 
		} 	
		grdRace.Redraw = true;

        return;
	}
  	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

