function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    //경주결과조회 
	var sServiceName = "e04040010-service:searchRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRace=dsOutRace";
    dsOutRace.ClearData();

    fce_AddParam(Trim("STND_YEAR"), 	cboSStndYear.Value	);
	fce_AddParam(Trim("MBR_CD   "), 	cboSMbr.Value     	);
//	fce_AddParam(Trim("TMS_FROM "), 	cboSRaceTms1.Value 	);
//	fce_AddParam(Trim("TMS_TO   "), 	cboSRaceTms2.Value  );
	fce_AddParam(Trim("TMS_FROM "), 	calRaceDt1.Value 	);
	fce_AddParam(Trim("TMS_TO   "), 	calRaceDt2.Value    );
    if ( chkExclude.Value = True ) {
        fce_AddParam(Trim("VOIL_CD  "), "Y"                	);
    } else {
        fce_AddParam(Trim("VOIL_CD  "), ""                 	);
    }
    fce_AddParam(Trim("SEARCH_COND  "), cboSearch.Value   	);
    fce_AddParam(Trim("SEARCH_VALUE "), edtSearch.text   	);

    fce_AddParam(Trim("SEARCH_TERM  "), chkRaceDay.Value   	);	//기간별로 조회시 년도가 무시된다.

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");

}

