{
	fcSearch();
}

function chkExclude_OnClick(obj,strValue)
{
	fcSearch();
}

function chkRaceDay_OnClick(obj,strValue)
{
    if ( chkRaceDay.Value = True ) {
		cboSRaceTms1.Enable = false;
		cboSRaceTms2.Enable = false;
		calRaceDt1.Enable = true;
		calRaceDt2.Enable = true;
		cboSStndYear.Enable = false;
	} else {
		cboSRaceTms1.Enable = true;
		cboSRaceTms2.Enable = true;
		calRaceDt1.Enable = false;
		calRaceDt2.Enable = false;
		cboSStndYear.Enable = true;
	}
}

function dsOutSDayOrd2_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsOutSDayOrd2.Row = dsOutSDayOrd2.GetLastRow();
}
