/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;
	// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fce_GetRaceTms(cboSStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
		return;
	}
	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        cboSRaceTms1.Index = 0;
        cboSRaceTms2.Index = 0;

        if ( cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) cboSRaceTms1.Index = nIndexRow;
            if ( nIndexRow > -1 ) cboSRaceTms2.Index = nIndexRow;
        }
        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms1.Value, "dsOutSDayOrd1");
        fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms2.Value, "dsOutSDayOrd2");
        fcSearch(); 
		return;
	}

	//상세현황조회후
	if ( sServiceName == "e04070010-service:searchRace1" ) {
        return;
	}
	//전체현화조회후
	if ( sServiceName == "e04070010-service:searchRace2" ) {
        return;
	}
	//회차별현황조회후
	if ( sServiceName == "e04070010-service:searchRace3" ) {
        return;
	}
	//일차별현황조회후
	if ( sServiceName == "e04070010-service:searchRace4" ) {
        return;
	}
	//경주별현황조회후
	if ( sServiceName == "e04070010-service:searchRace5" ) {
        return;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

