function grdRace6_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if( dsOutRace6.GetRowCount() <= 0 ) {
	   fnc_Message(L_COM_ALT_0024, "N", "조회된 결과가 없으므로 처리할수");
		return false;
	}
	// Content로 넘겨지는 arguements
	var sArg    = " Arg_cboSStndYear="  + quote(cboSStndYear.Value);
        sArg   += " Arg_cboSMbr="       + quote(cboSMbr.Value);
        sArg   += " Arg_cboSRaceTms1="  + quote(cboSRaceTms1.Value);
        sArg   += " Arg_cboSRaceTms2="  + quote(cboSRaceTms2.Value);

	if ( Dialog("form::E04070011.xml", sArg, 688, 360, "","1","1") ) ;
}

