function fcSearchCheck(obj) {

	fcInit(obj);
	if(fnc_isNull(tab0.tab1.cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            tab0.tab1.cboSStndYear.SetFocus();
            return false;
    }
    if(fnc_isNull(tab0.tab1.cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            tab0.tab1.cboSMbr.SetFocus();
            return false;
    } 
    if(fnc_isNull(tab0.tab1.cboSRaceTms.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            tab0.tab1.cboSRaceTms.SetFocus();
            return false;
    }
//    if(fnc_isNull(cboSDayOrd.Value)) {
//            fnc_Message(SNIS_COM_1034, "N", "일차");
//            cboSDayOrd.SetFocus();
//            return false;
//    } 
	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

    //경주기준정보조회
	var sServiceName = "e04090010-service:searchRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRace=dsOutRace";
    dsOutRace.ClearData();

    fce_AddParam(Trim("STND_YEAR"), tab0.tab1.cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), tab0.tab1.cboSDayOrd.Value  );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


     //경주기준정보조회(일차)
	var sServiceName = "e04090010-service:searchDayRace";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDayRace=dsOutDayRace";
    dsOutDayRace.ClearData();

    fce_AddParam(Trim("STND_YEAR"), tab0.tab1.cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), tab0.tab1.cboSDayOrd.Value  );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


    //경주상황조회(규칙위반/사고) 
	var sServiceName = "e04090010-service:searchVoilAcdnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilAcdnt=dsOutVoilAcdnt";
    dsOutVoilAcdnt.ClearData();

    fce_AddParam(Trim("STND_YEAR"), tab0.tab1.cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), tab0.tab1.cboSDayOrd.Value  );
	fce_AddParam(Trim("DAY_ORD_CNT"), dsOutSDayOrd.GetRowCount() - 1  );//전체 제외.

    //면책(체크박스)
    if ( tab0.tab1.chkExclude.Value = True ) {
        fce_AddParam(Trim("VOIL_CD  "), "Y"                	);
    } else {
        fce_AddParam(Trim("VOIL_CD  "), ""                 	);
    }

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

    //전회차대비현황
	var sServiceName = "e04090010-service:searchVoil";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoil=dsOutVoil";
    dsOutVoil.ClearData();

    fce_AddParam(Trim("STND_YEAR"), tab0.tab1.cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), tab0.tab1.cboSDayOrd.Value  );
    //면책(체크박스)
    if ( tab0.tab1.chkExclude.Value = True ) {
        fce_AddParam(Trim("VOIL_CD  "), "Y"                	);
    } else {
        fce_AddParam(Trim("VOIL_CD  "), ""                 	);
    }

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

    // 위반상황상세조회
    var sServiceName = "e04090010-service:SearchVoilDesc";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilDesc=dsOutVoilDesc";
    dsOutVoilDesc.ClearData();

    fce_AddParam(Trim("STND_YEAR"), tab0.tab1.cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), tab0.tab1.cboSDayOrd.Value  );
    //면책(체크박스)
    if ( tab0.tab1.chkExclude.Value = True ) {
        fce_AddParam(Trim("VOIL_CD  "), "Y"                	);
    } else {
        fce_AddParam(Trim("VOIL_CD  "), ""                 	);
    }
    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false); 

    // 위반상황상세조회
