	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );
	fce_AddParam(Trim("DAY_ORD  "), tab0.tab1.cboSDayOrd.Value  );
    //면책(체크박스)
    if ( tab0.tab1.chkExclude.Value = True ) {
