    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false); 

}

/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {

	// 최고 경수주를 구한다. 리포트에서 사용
	var maxRace = 15;

	if(fnc_IsNull(cboSDayOrd.Value)){  // 회차 전체 대상
		for(var i = 1; i < dsOutSDayOrd.GetRowCount(); i++){
			if(maxRace < toInteger(dsOutSDayOrd.GetColumn(i, "RACE_CNT")))
				maxRace =  dsOutSDayOrd.GetColumn(i, "RACE_CNT");
		}	
	}else{	// 일차 선택시
		maxRace = dsOutSDayOrd.GetColumn(fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", tab0.tab1.cboSDayOrd.Value), "RACE_CNT");
	}


    var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     						  //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e04090010_race_result_report.jrf";               //레포트 파일명
	var sDataSet  = "dsOutRaceReport=dsOutRace dsOutDayRaceReport=dsOutDayRace dsOutVoilAcdntReport=dsOutVoilAcdnt dsOutVoilReport=dsOutVoil  dsOutVoilDescReport=dsOutVoilDesc dsOutVoilDescDetailReport=dsOutVoilDescDetail";    //"UBI 쿼리명, MIP Dataset명"

	// fce_GetRaceDayOrd(cboSStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
    var raceDayFrom = dsOutSDayOrd.getColumn(1, "RACE_DAY");
    var raceDayTo   = dsOutSDayOrd.getColumn(2, "RACE_DAY");

	var dayOrdVal = cboSDayOrd.Value;
	if ( fnc_IsNull(dayOrdVal) ) {
	    dayOrdVal = "전체";
	} else {
	    dayOrdVal = "(제" + tab0.tab1.cboSDayOrd.Value +"일차)";
	}

	var sArg  = " TMS="+tab0.tab1.cboSRaceTms.Value;
		sArg += " RACE_DAY_FROM="+raceDayFrom;
		sArg += " RACE_DAY_TO="+raceDayTo;
		sArg += " DAY_ORD="+dayOrdVal;
		sArg += " IMG_URL="+logImgUrl;
		sArg += " MAX_RACE="+maxRace;
	var sRptDrct  = 1;                     //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

}
