/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

// 공통코드 조회시
	if ( sServiceName == "common-service:searchCode" ) {
        tab0.tab1.cboSMbr.Index = 0;
        tab0.tab1.cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fce_GetRaceTms(tab0.tab1.cboSStndYear.Value, tab0.tab1.cboSMbr.Value, "", "dsOutSRaceTms");
		return;
	}
	// 회차 리스트 조회시
	if ( sServiceName == "common_race-service:searchRaceTms" ) {
        tab0.tab1.cboSRaceTms.Index = 0;

        if ( tab0.tab1.cboSStndYear.Value == fce_GetCurRaceInfo("STND_YEAR") ) {
            var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", fce_GetCurRaceInfo("TMS"));
            if ( nIndexRow > -1 ) tab0.tab1.cboSRaceTms.Index = nIndexRow;
         }

        fce_GetRaceDayOrd(tab0.tab1.cboSStndYear.Value, tab0.tab1.cboSMbr.Value, tab0.tab1.cboSRaceTms.Value, "dsOutSDayOrd");
		return;
	}
	// 일차 리스트 조회 후
	if ( sServiceName == "common_race-service:searchRaceDayOrd" ) {
        tab0.tab1.cboSDayOrd.Index = 0;

        if ( tab0.tab1.cboSRaceTms.Value == CURR_TMS ) {
            var nIndexRow = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", CURR_DAY_ORD);

            if ( nIndexRow > -1 ) tab0.tab1.cboSDayOrd.Index = nIndexRow;
        }

        var befordDate = fnc_calDate(dsOutSDayOrd.GetColumn("0","RACE_DAY"),1,"Y");
        dsOutSDayOrd.row = fnc_GetExistValue(dsOutSDayOrd, "DAY_ORD", tab0.tab1.cboSDayOrd.Value);

        //코드값 추가, 전체일자 조회 날짜 세팅---------------------------------------------------//
        var nRow = 0;
        bFirst = "";

        if ( !bFirst ) {
            dsOutSDayOrd.InsertRow(nRow);
        } else {
            nRow = dsOutSDayOrd.GetRowCount();
            dsOutSDayOrd.AddRow();
        }

        dsOutSDayOrd.SetColumn(nRow, "DAY_ORD" , "");
        dsOutSDayOrd.SetColumn(nRow, "RACE_DAY" , befordDate);

        if ( tab0.tab1.cboSDayOrd != null ) 
            tab0.tab1.cboSDayOrd.index = nRow;



        fcSearch();
		return;
	}

    // 경주결과보고조회후
	if ( sServiceName == "e04090010-service:searchRace" ) {

        if( dsOutRace.GetRowCount() > 0 ) {
		 //총인원구함 
		 var racerCnt 	  = dsOutRace.GetColumn(0, "RACER_CNT" ); //선수수
		 var refereeCnt   = tab0.tab1.edtRefereeCnt.Text;
		 var totcnt = ToNumber(racerCnt)  + ToNumber(refereeCnt) ;
		 tab0.tab1.stTotCnt.Text = totcnt;

		 dsOutRace.SetColumn(0, "TOT_CNT", 			    totcnt );  			   //총운영인력 		 

		 dsOutRace.SetColumn(0, "REFEREE_CNT", 			tab0.tab1.edtRefereeCnt.value  );  //경주운영보조원
		 dsOutRace.SetColumn(0, "REFEREE_SUB", 			tab0.tab1.edtRefereeSub.value  );  //경주운영보조원
		 dsOutRace.SetColumn(0, "REFEREE_CONT", 		tab0.tab1.edtRefereeCont.value ); //중앙심판
		}

    }

    // 규칙/위반사고조회후
	if ( sServiceName == "e04090010-service:searchVoilAcdnt" ) {

       return;
    }

    // 전회차대비현황조회후
	if ( sServiceName == "e04090010-service:SearchVoil" ) {

       return;
    }

    // 경주기준정보조회(일차)
	if ( sServiceName == "e04090010-service:searchDayRace" ) {

		var maxRaceNo = 15;

		for(var i = 0; i < dsOutDayRace.GetRowCount(); i++){
			if(dsOutDayRace.GetColumn(i, "SUM_RACE_NO") != "" 
				&& toInteger(dsOutDayRace.GetColumn(i, "SUM_RACE_NO")) > maxRaceNo){
					maxRaceNo = toInteger(dsOutDayRace.GetColumn(i, "SUM_RACE_NO"));
			}
		}

		setGridSize(maxRaceNo);

       return;
    }

    // 위반내용상세조회후(경고-210,주위(제외)-220
	if ( sServiceName == "e04090010-service:SearchVoilDesc" ) {

        var voilCount 	= 0;
		var printCount  = 0;
		var voilCd		= "";
		var voilNm		= ""; 
		var voilDesc	= "";

		var voilHeader  = "";
		var voilFooter  = "";
		var voilContent = "";
		tab0.tab1.taVoilAct.Text =  "";

	    if( dsOutVoilDesc.GetRowCount() > 0 ) {
		  for ( i = 0; i < dsOutVoilDesc.GetRowCount(); i++ ) { 

		     //맨처음
			 if(  i == 0  ) {
			    voilCd 	  = dsOutVoilDesc.GetColumn(i, "VOIL_CD" 	); 
			 }

		     if(voilCd == dsOutVoilDesc.GetColumn(i, "VOIL_CD" 	)) {

			     voilCount++;
			     voilHeader   = "";
			     voilFooter   = voilFooter + dsOutVoilDesc.GetColumn(i, "VOIL_DESC" 	)  + "\n";

			 } else {

			    voilHeader   =  "ㅇ" + voilNm + " : " + voilCount + '건'   + "\n"; 
			    voilContent  = voilContent +  voilHeader + voilFooter ;

			    voilFooter = "";
			    voilFooter   =  voilFooter + dsOutVoilDesc.GetColumn(i, "VOIL_DESC" 	)  + "\n";


			    voilCount = 0  ;
			    voilCount++    ;

			 } 
			 //맨마지막


			 if(  (i + 1) == dsOutVoilDesc.GetRowCount() ) {


			    voilHeader   =  "ㅇ" + dsOutVoilDesc.GetColumn(i, "VOIL_NM" 	) + " : " + voilCount + '건'   + "\n"; 
			    voilContent  = voilContent +  voilHeader + voilFooter ;

			 }


			 voilCd 	=  dsOutVoilDesc.GetColumn(i, "VOIL_CD" 	) ;
             voilNm		=  dsOutVoilDesc.GetColumn(i, "VOIL_NM" 	) ; 
		     voilDesc	=  dsOutVoilDesc.GetColumn(i, "VOIL_DESC" 	) ;

			  }
		}

        if( dsOutVoilDesc.GetRowCount() > 0 ) {
            tab0.tab1.taVoilAct.Text = voilContent;
        }  
    }
 }   
