function btn_result_print_OnClick(obj)
{
    http.sync = true;
    if (tab0.TabIndex == 0) {
		fcSearchReportData();
	} else {
		fcSearchReportDataTerm();
	}
	http.sync = false;

	// 출력
    var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sSubUrl   = "";                     						  //레포트 파일이 있는 서브디렉토리
	var sRptNm    = "";               //레포트 파일명
	var sDataSet  = "";
	var sArg      = "";
    if (tab0.TabIndex == 0) {			// tab0 = '회차별' 경주결과보고서 출력
		sSubUrl   = "";                     						  //레포트 파일이 있는 서브디렉토리
		sRptNm    = "e04090010_race_result_print.jrf";               //레포트 파일명

		if(dsOutSRaceTms.GetColumn(tab0.tab1.cboSRaceTms.Index, "MAX_RACE_REG_NO_CNT") > 6){
			sRptNm    = "e04090010_race_result_print8.jrf";		// 8인제 경정 시행시 대비
		}

		//alert(sRptNm);

		sDataSet  = "VoilDay=dsOutVoilDay "
					  + "VoilGrd=dsOutVoilGrd "
					  + "VoilJo=dsOutVoilJo "
					  + "VoilLoc=dsOutVoilLoc "
					  + "Acdnt=dsOutAcdnt "
					  + "Course=dsOutCourse "
					  + "AvgST=dsOutAvgST "
					  + "PerioST=dsOutPerioST "
					  + "GrdST=dsOutGrdST "
					  + "CourseST=dsOutCourseST "
					  + "STDist=dsOutSTDist "
					  + "FL=dsOutFL ";		//"UBI 쿼리명, MIP Dataset명"

		sArg  = " TMS="+tab0.tab1.cboSRaceTms.Value; 
			sArg += " FL_CNT="+dsOutFL.RowCount(); 
			sArg += " IMG_URL="+logImgUrl; 
	} else {						// !tab0  = tab1 = '기간별' 경주결과보고서 출력
	    sRptNm    = "e04090010_race_result_term_print.jrf";               //레포트 파일명
	    sDataSet  = "VoilDay=dsOutVoilDay_0 "
                  + "VoilGrd=dsOutVoilGrd_0 "
                  + "VoilJo=dsOutVoilJo_0 "
                  + "VoilLoc=dsOutVoilLoc_0 "
                  + "Acdnt=dsOutAcdnt_0 "
                  + "Course=dsOutCourse_0 "
                  + "AvgST=dsOutAvgST_0 "
                  + "PerioST=dsOutPerioST_0 "
                  + "GrdST=dsOutGrdST_0 "
                  + "CourseST=dsOutCourseST_0 "
                  + "STDist=dsOutSTDist_0 "
                  + "FL=dsOutFL_0 ";		//"UBI 쿼리명, MIP Dataset명"
        var strTerm  = "(" + mid(tab0.tab2.calRaceStartDt.Value,0,4) + "." + mid(tab0.tab2.calRaceStartDt.Value,4,2) + "." + mid(tab0.tab2.calRaceStartDt.Value,6) + "~";
            strTerm +=       mid(tab0.tab2.calRaceEndDt.Value,0,4) + "." + mid(tab0.tab2.calRaceEndDt.Value,4,2) + "." + mid(tab0.tab2.calRaceEndDt.Value,6) + ")";
	    sArg  = " TMS="    + strTerm; 
		sArg += " FL_CNT=" + dsOutFL_0.RowCount(); 
		sArg += " IMG_URL="+ logImgUrl; 
	}
	var sRptDrct  = 1;                               //출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

}

/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 입소기록표정보 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchReportData(obj) {
	var sServiceName = "e04090010-service:searchPrintVoilDay";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilDay=dsOutVoilDay "
                     + "dsOutVoilGrd=dsOutVoilGrd "
                     + "dsOutVoilJo=dsOutVoilJo "
                     + "dsOutVoilLoc=dsOutVoilLoc "
                     + "dsOutAcdnt=dsOutAcdnt "
                     + "dsOutCourse=dsOutCourse "
                     + "dsOutAvgST=dsOutAvgST "
                     + "dsOutPerioST=dsOutPerioST "
                     + "dsOutGrdST=dsOutGrdST "
                     + "dsOutCourseST=dsOutCourseST "
                     + "dsOutSTDist=dsOutSTDist "
                     + "dsOutFL=dsOutFL ";

    fce_AddParam(Trim("STND_YEAR"), tab0.tab1.cboSStndYear.Value);
	fce_AddParam(Trim("MBR_CD   "), tab0.tab1.cboSMbr.Value     );
	fce_AddParam(Trim("TMS      "), tab0.tab1.cboSRaceTms.Value );

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false); 

	//alert(cboSRaceTms.Value + "회차 판정결과 자료입니다.");
}

/*-------------------------------------------------------------------------------------------------+
>>  회차별 최고경주수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setGridSize(maxRaceNo){
	var nMaxRaceCnt = 18;
	var nMaxRaceNo = maxRaceNo;

	//Alert("nMaxRaceNo : " + nMaxRaceNo);

	tab0.tab1.grdVoilAcdnt.Redraw = false;

	var nCol0W = tab0.tab1.grdVoilAcdnt.GetColProp(0, "Width");
	var nCol1W = tab0.tab1.grdVoilAcdnt.GetColProp(1, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxRaceCnt; i++ ) {
        tab0.tab1.grdVoilAcdnt.SetColProp(2 + i, "Width", 25);
    }

    //nMaxRaceCnt 부터 nMaxRaceNo까지 사이즈를 줄임.
    for ( var i = nMaxRaceCnt; i > nMaxRaceNo; i-- ) {
        tab0.tab1.grdVoilAcdnt.SetColProp(2 + i-1, "Width", 0);
    }

    tab0.tab1.grdVoilAcdnt.SetColProp(0, "Width", nCol0W);
    tab0.tab1.grdVoilAcdnt.SetColProp(1, "Width", nCol1W);

    tab0.tab1.grdVoilAcdnt.Redraw = true;
}


/*-------------------------------------------------------------------------------------------------+
>>  레포트를 위한 기간별 입소기록표정보 가져오기
+-------------------------------------------------------------------------------------------------*/
function fcSearchReportDataTerm(obj) {
	var sServiceName = "e04090010-service:searchPrintVoilDayTerm";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilDay_0=dsOutVoilDay "
                     + "dsOutVoilGrd_0=dsOutVoilGrd "
                     + "dsOutVoilJo_0=dsOutVoilJo "
                     + "dsOutVoilLoc_0=dsOutVoilLoc "
                     + "dsOutAcdnt_0=dsOutAcdnt "
                     + "dsOutCourse_0=dsOutCourse "
                     + "dsOutAvgST_0=dsOutAvgST "
                     + "dsOutPerioST_0=dsOutPerioST "
                     + "dsOutGrdST_0=dsOutGrdST "
                     + "dsOutCourseST_0=dsOutCourseST "
                     + "dsOutSTDist_0=dsOutSTDist "
                     + "dsOutFL_0=dsOutFL ";

    fce_AddParam(Trim("STR_DT"), tab0.tab2.calRaceStartDt.Value);
    fce_AddParam(Trim("END_DT"), tab0.tab2.calRaceEndDt.Value);

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false); 
}


function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	if (tab0.tabindex == 1) {
		divBtnLst.btnPrint.enable = false;
	} else {
		divBtnLst.btnPrint.enable = true;
	}
}
