function fcSearchCheck(obj) {
	var sDt  = cboSStndYear.Value;
	var sTms = cboSRaceTms.Value;
	var eTms = cboERaceTms.Value;

	if(    fnc_isNull(sDt) ) {
        fnc_message(L_COM_ALT_0024, "N", "시작년도가 ");
        cboSStndYear.Value = "";
        return false;
	}	
/*	
	if(    !fnc_isNull(sTms) 
        && !fnc_isNull(eTms)
        && toInteger(sTms) > toInteger(eTms) ) {
        fnc_message(L_COM_ALT_0024, "N", "시작회차가 종료회차보다 이후일 수");
        return false;
	}		
*/
    fcInit(obj);
 	return true;
}

function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "e04090020-service:searchRacerRankAcdnt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerRankAcdnt=dsOutRacerRankAcdnt ";

	fce_AddParam("STR_DT"    , calStrDt.Value);
	fce_AddParam("END_DT"    , calEndDt.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


