function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit(obj);
    fce_GetRaceTms(cboSStndYear.value, cboSMbr.Value, "", "dsOutSRaceTms");
    gF_TmsGubun = "S";
}

function cboEStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    fcInit(obj);
    fce_GetRaceTms(cboEStndYear.value, cboEMbr.Value, "", "dsOutERaceTms");
    gF_TmsGubun = "E";
}

function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutSRaceTms, "TMS", cboSRaceTms.Value);
    calStrDt.Value = dsOutSRaceTms.GetColumn(nIndexRow, "STR_DT");
}

function cboERaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    var nIndexRow = fnc_GetExistValue(dsOutERaceTms, "TMS", cboERaceTms.Value);
    calEndDt.Value = dsOutERaceTms.GetColumn(nIndexRow, "END_DT");
}

/*-------------------------------------------------------------------------------------------------+
>>  년도별 최고정번수에 따른 그리드 사이즈 조절
+-------------------------------------------------------------------------------------------------*/
function setGridSize(){
	/*
		IWORK_SFR-008 경정 선수편성 메뉴 개선  정번8 까지 추가 수정.
		회차에 따른 최고(8까지)정번수 별로 1 ~ 정번수 까지 화면에 표시.
	*/
	var nMaxTmsRunCnt = 8;
	var nMaxRaceRegNoCnt = -1;

	nMaxRaceRegNoCnt = dsOutRacerRankAcdnt.GetColumn(0, "MAX_RACE_REG_NO_CNT");

	if(nMaxRaceRegNoCnt == -1 || fnc_IsNull(nMaxRaceRegNoCnt)) nMaxRaceRegNoCnt = 6;

	//Alert("nMaxRaceRegNoCnt : " + nMaxRaceRegNoCnt);

	grdRacerList.Redraw = false;

	var nCol0W = grdRacerList.GetColProp(0, "Width");
	var nCol1W = grdRacerList.GetColProp(1, "Width");
	var nCol2W = grdRacerList.GetColProp(2, "Width");

	//전체적으로 사이즈를 넓힘.
	for ( var i = 0; i < nMaxTmsRunCnt; i++ ) {
        grdRacerList.SetColProp(5 + i, "Width", 50);
    }

    //nMaxTmsRunCnt 부터 nMaxRaceRegNoCnt까지 사이즈를 줄임.
    for ( var i = nMaxTmsRunCnt; i > nMaxRaceRegNoCnt; i-- ) {
        grdRacerList.SetColProp(5 + i-1, "Width", 0);
    }

    grdRacerList.SetColProp(0, "Width", nCol0W);
    grdRacerList.SetColProp(1, "Width", nCol1W);
    grdRacerList.SetColProp(2, "Width", nCol2W);

    grdRacerList.Redraw = true;
}
