//조회 
function fcSearch(obj) {
	fcSearchCheck();

    var sServiceName = "e05040010-service:searchRacerSanc";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerSanc=dsOutRacerSanc";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("SANC_DEPT", cboSancDept.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	// 심판(S)이  공정(SANC_DEPT=G)의 내역을 저장하지 못하도록 처리
	for(var i = 0; i < dsOutRacerSanc.GetRowCount(); i++){
		if(dsOutRacerSanc.GetColumn(i, "CHK") == "1" && dsOutRacerSanc.GetColumn(i, "SANC_DEPT") == "G") {
			fnc_Message("공정의 제재처분등록 내역은 심판에서 저장할 수 없습니다.", "N");
			return;
		}
	}

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerSanc))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerSanc;
	sMandatoryColID   = "RACER_NO|SANC_BASIS_CD|UNIT|SMRY|SANC_RSN_CD|SANC_RSN|SANC_ACT_DT|HOMEPAGE_NOTI_SDT|HOMEPAGE_NOTI_EDT";
	sMandatoryColName = "등록번호|양정코드|제재단위|관련근거|사유|사유내역|처분일자|공지시작일|공지종료일";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	SAVE_OBJ_ID = obj.id;

	var sServiceName = "e05040010-service:saveRacerSanc";
    var sInDataSet   = "dsOutRacerSanc=dsOutRacerSanc:u";
    var sOutDataSet  = "dsOutRacerSanc=dsOutRacerSanc";

	//fce_AddParam("STND_YEAR", cboStndYear.Value);

	// 제재확정시
    if ( obj.id == "btnConfirm" ) {
        fce_AddParam(Trim("CFM"), "Y");
    } else {
        fce_AddParam(Trim("CFM"), "N");
    }

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

