/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var nRow = dsOutRacerSanc.GetRowCount();
	var nAddRow;
	var nFindRow;

	dsOutRacerSanc_Report.ClearData();
	for (var i = 0; i < nRow; i++)
	{
		if (dsOutRacerSanc.GetColumn(i, "CHK") == "1")
		{
			nAddRow = dsOutRacerSanc_Report.AddRow();
			dsOutRacerSanc.CopyToRow(i, dsOutRacerSanc_Report, nAddRow);
			nFindRow = dsOutRsnCd.FindRow("CD", dsOutRacerSanc_Report.GetColumn(nAddRow, "SANC_RSN_CD"));
			dsOutRacerSanc_Report.SetColumn(nAddRow, "SANC_RSN_CD", dsOutRsnCd.GetColumn(nFindRow, "CD_NM"));
		}
	}

	if (dsOutRacerSanc_Report.GetRowCount() == 0)
	{
		fnc_Message(SNIS_BOA_E005, "N");
		return;
	}
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sealImgUrl = GBL_REPORT_URL+"seal2.jpg";
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e05049010_racer_sanc.jrf";        	//레포트 파일명
	var sDataSet  = "dsOutRacerSanc=dsOutRacerSanc_Report";   	//"UBI 쿼리명, MIP Dataset명"
	var sArg      = "IMG_URL="+logImgUrl + " SEAL_IMG_URL="+sealImgUrl;

	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);

}
