/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if (dsOutRacerSanc.ColCount() > 0) {
		var nRow = dsOutRacerSanc.AddRow();        	  //Record Add   cboStndYear.Value
		dsOutRacerSanc.SetColumn(nRow, "STND_YEAR", cboStndYear.Value);
		dsOutRacerSanc.SetColumn(nRow, "SANC_ISSUE_NO", "*저장시 생성");

		// 제재입력부서 코드값 setting(공정팀=G, 심판팀=S)
		dsOutRacerSanc.SetColumn(nRow, "SANC_DEPT", "S");

		cboSMbr.SetFocus();
	} else {
		fnc_Message(SNIS_COM_0002, "N");      //DataSet의 Header가 없어서 오류
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {

	for(var i = 0; i < dsOutRacerSanc.GetRowCount(); i++){
		if(dsOutRacerSanc.GetColumn(i, "CHK") == "1" && dsOutRacerSanc.GetColumn(i, "CFM") == "Y") {
			fnc_Message("확정등록된 자료는 삭제할 수 없습니다. 확인하시고 처리하시기 바랍니다.", "N");
			return;
		}
	}

	//DataSet의 Header 정보가 있을 때  Add 처리 	
	if ( !fnc_DeleteCheck(dsOutRacerSanc, "CHK", "1") ) return;
	grdPageList.SetFocus();                                  //Record 삭제 후 Grid로 Focus

	enableDisableCheck(dsOutRacerSanc.GetCurrow());
}

