/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
	// 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
		fce_GetRaceTms(cboStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
		fcSearch();
	}

	// 제재처분 조회 후
	if ( sServiceName == "e05040010-service:searchRacerSanc" ) {
	    if(dsOutRacerSanc.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutRacerSanc.GetRowCount());
	    }

		//조회 후 해당 데이타셋의 첫번째행을 선택
		dsOutRacerSanc.row = 0;
	}

	// 저장 후
	if ( sServiceName == "e05040010-service:saveRacerSanc" ) {
		if(dsOutRacerSanc.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutRacerSanc.GetRowCount());
	    }

	    // 확정 처리시 문자 전송
	    if (SAVE_OBJ_ID == "btnConfirm") fcSendSMS();

	    fcSearch();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	switch(sServiceName) {
		case "common_help-service:searchSancBasis" :
		case "common_help-service:searchRacer" :
			//코드명 조회 후 처리
			fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
//			var oFocusComponent = GetNextComponent(true);
//			oFocusComponent.setfocus();
			break;
	}
}


/*-------------------------------------------------------------------------------------------------+
>>  버튼클릭시 조회 서비스
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   

	// 초기화
	sCUROBJ  = object(obj.id);
	sGRDTIT  = "";

	switch(obj.id) {
		case "btnRacerNo" :                   				//선수 등록번호
			vREVOBJ[0]   = "edtRACER_NO";        			//선수 등록번호를 받을 Text Object명
			vREVOBJ[1]   = "edtNM_KOR";        				//성명을 받을 Text Object명
			vREVOBJ[2]   = "edtRACER_GRD_CD";       		//등급을 받을 Text Object명
			sGRDTIT      = "등록번호:성명:등급";			//그리드 제목

			sServiceName = "common_help-service:searchRacer";
			sInDataSet   = "";                      		//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutRacer";			//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("RACER_NO"), Trim(edtRACER_NO.Text));
			break;
		case "btnSancBasis" :									//양정 코드 
			vREVOBJ[0]   = "edtSANC_BASIS_CD";					//부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edtSB_SMRY";    					//관련 근거를 받을 Text Object명
			vREVOBJ[2]   = "edtSANC_TERM";        				//제재처분기간을 받을 Text Object명
			vREVOBJ[4]   = "cboUNIT";        					//단위를 받을 Text Object명
			sGRDTIT      = "양정코드:관련근거:기간:단위:코드";	//그리드 제목

			sServiceName = "common_help-service:searchSancBasis";
			sInDataSet   = "";								//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutSancBasis";	//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("SANC_BASIS_CD"), Trim(edtSANC_BASIS_CD.Text));
			break;
	}

	//조회 서비스 실행
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


//row 변경시 
function dsOutRacerSanc_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	enableDisableCheck(dsOutRacerSanc.GetCurrow());

	fce_GetRaceTms(cboStndYear.Value, dsOutRacerSanc.GetColumn(nRow, "MBR_CD"), "", "dsOutSRaceTms");
	fce_GetRaceDayOrd(cboStndYear.Value, dsOutRacerSanc.GetColumn(nRow, "MBR_CD"), dsOutRacerSanc.GetColumn(nRow, "TMS"), 
					  "dsOutSDayOrd");
	fce_GetRace(cboStndYear.Value, dsOutRacerSanc.GetColumn(nRow, "MBR_CD"), dsOutRacerSanc.GetColumn(nRow, "TMS"), 
				dsOutRacerSanc.GetColumn(nRow, "DAY_ORD"), "dsOutSRace");
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutRacerSanc_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutRacerSanc.Row = LATEST_ROW_POS;
}


function fcSendSMS(obj) {

	var iSelectRowCount = fnc_GetColumnValueCount(dsOutRacerSanc, "CHK", "1");

	if ( iSelectRowCount <= 0 ) {
		fnc_Message(SNIS_COM_1036, "N");
		return false;
	}
	var sSendPhoneNumber 	= gdsUser.GetColumn(0,"HP_NO");	 //송신자이동전화번호 
	    sSendPhoneNumber 	= gdsUser.GetColumn(0,"TEL_NO");	 //송신자 사무실 전화번호(변경요청:김해선,2014.7.13)
	var sSendUserId 	 	= gdsUser.GetColumn(0,"USER_ID");//송신자 사번
	var sSendUserName 		= gdsUser.GetColumn(0,"USER_NM");//송신자명 
	var sReceivePhoneNumber = "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sReceiveName 		= "";							 //수신자이동전화번호 , 여러명인경우 ; 로 구분 
	var sParams				= "";
	var sReceiveId          = "";
	var sTitle              = "제재처분대상자 문자 발송";

	for(var i = 0; i <dsOutRacerSanc.GetRowCount();i++){
		if(dsOutRacerSanc.GetColumn(i,"CHK") == "1"){		
			sReceivePhoneNumber +=  dsOutRacerSanc.GetColumn(i,"HP_NO")+";";
			sReceiveName        +=  dsOutRacerSanc.GetColumn(i,"NM_KOR")+";";
			sReceiveId          +=  dsOutRacerSanc.GetColumn(i,"RACER_NO")+";";
		}			

	}
	sReceivePhoneNumber = substr(sReceivePhoneNumber,0,LENGTH(sReceivePhoneNumber)-1);
	sReceiveName        = substr(sReceiveName,0,LENGTH(sReceiveName)-1);
	sReceiveId          = substr(sReceiveId,0,LENGTH(sReceiveId)-1);

	sParams  = "sSendPhoneNumber="+quote(sSendPhoneNumber);
	sParams += " sSendUserId="+quote(sSendUserId);
	sParams += " sSendUserName="+quote(sSendUserName);
	sParams += " sReceivePhoneNumber="+quote(sReceivePhoneNumber);
	sParams += " sReceiveName="+quote(sReceiveName);
	sParams += " sReceiveId="+quote(sReceiveId);
	sParams += " sTitle="+quote(sTitle);

	//nHandle = Open("common::comSendSms.xml", sParams, 240, 328, "Title=false");

	// 문구 입력 팝업 대신 직접 발송으로 변경
	var sSMSMessage     = "[경정 제재처분 안내]\n 선수커뮤니티 사이트에서 확인 및 출력이 가능합니다.\n http://racer.kboat.or.kr/ \n -경정공정팀-";	
	sReceivePhoneNumber = "";
	sReceiveName        = "";
	sReceiveId          = "";
	for(var i = 0; i <dsOutRacerSanc.GetRowCount();i++){
		if(dsOutRacerSanc.GetColumn(i,"CHK") == "1"){		
			sReceivePhoneNumber +=  dsOutRacerSanc.GetColumn(i,"HP_NO") +",";
			sReceiveName        +=  dsOutRacerSanc.GetColumn(i,"NM_KOR")+",";
			sReceiveId          +=  dsOutRacerSanc.GetColumn(i,"RACER_NO")+",";
		}	
	}
	// TEST
	//sReceivePhoneNumber =  "010-9065-4606"+",";
	//sReceiveName        =  "신재선"+",";
	//sReceiveId          =  "01047"+",";
	fce_SendSms(  sSendPhoneNumber			// 송신자 번호
				, sSendUserId				// 송신자 사번 
				, sSendUserName	    		// 송신자 성명
				, sReceivePhoneNumber     	//수신자 번호
				, sReceiveName 	    		//수신자명
				, sTitle					// 제목
				, sReceiveName				//sReceiveId				// 수신자 사번
				, sSMSMessage				// sms 내용
				, ""						//예약발송일시		
				, sFormId);
}

function fcOnChanged(obj,strText)
{
	if (obj.id == "calHOMPAGE_NOTI_SDT" || obj.id == "edtSANC_TERM" || obj.id == "cboUnit") {
		if (!fnc_IsNull(calHOMPAGE_NOTI_SDT.Value) &&
		    !fnc_IsNull(edtSANC_TERM.Value) &&
		    !fnc_IsNull(cboUnit.Value) ) {
				var nDays = 0;
				var sStrDy = calHOMPAGE_NOTI_SDT.Value;
				var sEndDy = "";
				switch (toNumber(cboUnit.Value)) {
					case 2 :	//일
						sEndDy = AddDate(sStrDy, toNumber(edtSANC_TERM.Value) -1);
						break;
					case 3 :	//년
						sEndDy = AddDate(AddMonth(sStrDy, toNumber(edtSANC_TERM.Value) * 12), -1);
						break;
					case 4 :	//개월
						sEndDy = AddDate(AddMonth(sStrDy, toNumber(edtSANC_TERM.Value)), -1);
						break;
					default:
				}
				if (sEndDy != "") calHOMPAGE_NOTI_EDT.Value = sEndDy;
		 }		    
	} else if (obj.id == "calSANC_ACT_DT") {
		calHOMPAGE_NOTI_SDT.Value = calSANC_ACT_DT.Value;
	}
}

function edit_OnCharChanged(obj,strPreText,strPostText)
{
	if(Length(obj.text) <= 0) {
		if(obj.id == "edtRACER_NO") {
			edtNM_KOR.Text = "";
			edtRACER_GRD_CD.Text = "";
		}
		if(obj.id == "edtSANC_BASIS_CD") {
			edtSB_SMRY.Text = "";
			edtSANC_TERM.Text = "";
			cboUnit.Text = "";
		}
	}

}


function enableDisableCheck(nRow)
{
	if(dsOutRacerSanc.GetColumn(nRow, "CFM") != "Y") {
		edtRACER_NO.Enable = true;
		btnRacerNo.Enable = true;
		edtSANC_BASIS_CD.Enable = true;
		btnSancBasis.Enable = true;
		edtSANC_TERM.Enable = true;
		cboUnit.Enable = true;
		cboSANC_RSN_CD.Enable = true;
		calSANC_ACT_DT.Enable = true;
		calHOMPAGE_NOTI_SDT.Enable = true;
		calHOMPAGE_NOTI_EDT.Enable = true;
		edtSB_SMRY.Enable = true;
		edtSMRY.Enable = true;
		edtHOME_SANC_RSN.Enable = true;
		cboSMbr.Enable = true;
		cboSRaceTms.Enable = true;
		cboSDayOrd.Enable = true;
		cboSRaceNo.Enable = true;
	} else{
		edtRACER_NO.Enable = false;
		btnRacerNo.Enable = false;
		edtSANC_BASIS_CD.Enable = false;
		btnSancBasis.Enable = false;
		edtSANC_TERM.Enable = false;
		cboUnit.Enable = false;
		cboSANC_RSN_CD.Enable = false;
		calSANC_ACT_DT.Enable = false;
		calHOMPAGE_NOTI_SDT.Enable = false;
		calHOMPAGE_NOTI_EDT.Enable = false;
		edtSB_SMRY.Enable = false;
		edtSMRY.Enable = false;
		edtHOME_SANC_RSN.Enable = false;
		cboSMbr.Enable = false;
		cboSRaceTms.Enable = false;
		cboSDayOrd.Enable = false;
		cboSRaceNo.Enable = false;
	}		
}

//경정장변경시
function cboSMbr_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboSMbr.Value, "", "dsOutSRaceTms");
}

//회차변경시
function cboSRaceTms_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceDayOrd(cboStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, "dsOutSDayOrd");
}

//일차변경시
function cboSDayOrd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRace(cboStndYear.Value, cboSMbr.Value, cboSRaceTms.Value, cboSDayOrd.Value, "dsOutSRace");
}
