var sFormId       = 'E04170010';
var sBUTTONLIST   = 'FFFTFT';                          //공통기능정의(버튼순으로 T or F)
var sUSERAUTH     = '';                                //사용자별 프로그램 사용 권한
var sFORMCAPTION  = '';                                //Form의 Title 정보
var sFORMLOCATION = '';                                //Form의 Location 정보
var sUSERLAVEL    = '';                                //Form의 사용  Level
var vREVOBJ       = Array(9);                          //결과값 처리를 위한 오브젝트 
var sGRDTIT       = "";                                //그리드 제목
var sCUROBJ       ;                                    //현재 LOST FOCUS 오브젝트

var oLastDs       = null;
var oLastDsRow    = 0;

/***************************************************************************************************
*                                         공통 Event 처리 부분                                     *
***************************************************************************************************/

// 최초 화면 Load時 처리 할 사항
function fcFormLoadSetting(obj) {

	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

	//초기 화면 BK Color설정 
	fnc_SetBKColor(this);

    //년도조회
    fce_SetStndYear(dsOutSStndYear, toInteger(fce_GetSysdate("YYYY")));
    cboSStndYear.Index = 0;

	//공통코드 조회
	fce_GetComboDs(dsInComCd);

	http.sync= false; 
}


// 최초 화면 Load時 처리 할 사항
function fcFormUnloadProcess(obj) {
	//자료 변경 여부를 체크하고자 하는  DataSet Object를 넘긴다.
	var sDataset = "";
	if ( !fnc_FormUnloadCheck(sDataset , sFormId) ) return false;
}


/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/


// 년도콤보 변경시 이벤트
function cboSStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcGetRaceTms(cboSStndYear.Value, cboSMbr.Value, "dsOutSRaceTms");
}


//  엑셀 버튼 클릭 時 처리
function fcToExcel(obj) {
	fnc_OpenExcelExport("grdWeather");   
}

// 조회버튼 이벤트
function fcSearch(obj) {
    var sServiceName = ""; 
    var sInDataSet   = "";
    var sOutDataSet  = "";


    if ( !fcSearchCheck(obj) ) return;

	sServiceName = "e04170010-service:searchWeather";
	sInDataSet   = "";
	sOutDataSet  = "dsWeatherList=dsWeatherList";

    fce_AddParam(Trim("STND_YEAR"), 			cboSStndYear.Value		); //년도
	fce_AddParam(Trim("MBR_CD"), 				cboSMbr.Value     		); //경정장코드
	fce_AddParam(Trim("TMS"), 					cboSRaceTms.Value 		); //회차

    fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


// 조회버튼 클릭시 체크사항 처리
function fcSearchCheck(obj) {
	if(fnc_isNull(cboSStndYear.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "년도");
            cboSStndYear.SetFocus();
            return false;
    }

    if(fnc_isNull(cboSRaceTms.Text)) {
            fnc_Message(SNIS_COM_1034, "N", "회차");
            cboSRaceTms.SetFocus();
            return false;
    }

    if(fnc_isNull(cboSMbr.Value)) {
            fnc_Message(SNIS_COM_1034, "N", "경정장");
            cboSMbr.SetFocus();
            return false;
    } 

    return true;
}

//회차조회 처리
function fcGetRaceTms(sStndYear, sMbrCd, soutDS){
	var sServiceName = "e04170010-service:searchRaceTms";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutSRaceTms=dsOutSRaceTms";

	fce_AddParam(Trim("STND_YEAR"), sStndYear);
	fce_AddParam(Trim("MBR_CD"   ), sMbrCd   );

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


// CallBack함수
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 공통코드 조회 후
	if ( sServiceName == "common-service:searchCode" ) {
        cboSMbr.Index = 0;
        cboSMbr.Value = fce_GetUserInfo("MBR_CD");

        fcGetRaceTms(cboSStndYear.Value, cboSMbr.Value, "dsOutSRaceTms");
		return;
	}

	// 회차 조회 후 
	if ( sServiceName == "e04170010-service:searchRaceTms" ) {
        cboSRaceTms.Index = 0;
		return;
	}

	// 경주 기상정보자료 조회 후
	if ( sServiceName == "e04170010-service:searchWeather" ) {
        fnc_SetStatus(SNIS_BOA_0003, dsWeatherList.GetRowCount());
         return;
	}
}


// 그리드 정렬
function fcGridSort(obj,nCell,nX,nY) {
	//특정 Head 클릭 시 해당 항목으로 Sort(만약 특정 컬럼을 누를때만 소트하고자 한다면 nCell로 조건)
	fnc_GridSort(obj, object(obj.BindDataset), nCell);	
}


// 화면닫기 버튼
function fcEnd(obj) {
	Close();	
}
