/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(cboMbrCd, "경정장")) return false;
   if (!fnc_IsMandatory(cboFromTms, "회차")) return false;
   if (!fnc_IsMandatory(cboToTms, "마지막회차")) return false;
}

//조회 
function fcSearch(obj) {
	if(fcSearchCheck() == false) return;

    var sServiceName = "e05040080-service:searchVoilAct";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilAct=dsOutVoilAct";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("FROM_TMS", 1);
    fce_AddParam("TO_TMS", cboToTms.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
//상세조회 
function fcSearchDetail(obj,nOldRow,nRow) {

    var sServiceName = "e05040080-service:searchVoilActDetail";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutVoilActDetail=dsOutVoilActDetail";

    fce_AddParam("STND_YEAR", obj.GetColumn(nRow, "STND_YEAR"));
    fce_AddParam("MBR_CD", obj.GetColumn(nRow, "MBR_CD"));
    fce_AddParam("FROM_TMS", obj.GetColumn(nRow, "FROM_TMS"));
    fce_AddParam("TO_TMS", obj.GetColumn(nRow, "TO_TMS"));
    fce_AddParam("RACER_NO", obj.GetColumn(nRow, "RACER_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

