/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerSanc))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerSanc;
	sMandatoryColID   = "CHK";
	sMandatoryColName = "선택";
	oGrid             = tbDetail.tb1.grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "e05040080-service:saveRacerSanc";
    var sInDataSet   = "dsOutRacerSanc=dsOutRacerSanc:u";
    var sOutDataSet  = "dsOutRacerSanc=dsOutRacerSanc";

	var nRow = dsOutVoilAct.row;
	fce_AddParam("STND_YEAR", dsOutVoilAct.GetColumn(nRow, "STND_YEAR"));
    fce_AddParam("MBR_CD", dsOutVoilAct.GetColumn(nRow, "MBR_CD"));
    fce_AddParam("FROM_TMS", dsOutVoilAct.GetColumn(nRow, "FROM_TMS"));
    fce_AddParam("TO_TMS", dsOutVoilAct.GetColumn(nRow, "TO_TMS"));
    fce_AddParam("RACER_NO", dsOutVoilAct.GetColumn(nRow, "RACER_NO"));
    fce_AddParam("QURT_CD", dsOutVoilAct.GetColumn(nRow, "QURT_CD"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function fcConfirmCheck(obj) {
	// 필수항목체크
	// 필수항목체크
	oDataSet          = dsOutConfirm;
	sMandatoryColID   = "RACER_NO|SANC_BASIS_CD|SANC_TERM|SMRY|SANC_RSN_CD|SANC_RSN|SANC_ACT_DT|HOMEPAGE_NOTI_SDT|HOMEPAGE_NOTI_EDT";
	sMandatoryColName = "등록번호|양정코드|제재처분기간|관련근거|사유|사유내역|처분일자|공지시작일|공지종료일";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcConfirm(obj) {
    if ( !fcConfirmCheck() ) return;
	var sServiceName = "e05040080-service:saveConfirm";
    var sInDataSet   = "dsOutRacerSanc=dsOutConfirm:u";
    var sOutDataSet  = "dsOutRacerSanc=dsOutConfirm";

	var nRow = dsOutVoilAct.row;
	fce_AddParam("STND_YEAR", dsOutVoilAct.GetColumn(nRow, "STND_YEAR"));
    fce_AddParam("MBR_CD", dsOutVoilAct.GetColumn(nRow, "MBR_CD"));
    fce_AddParam("FROM_TMS", dsOutVoilAct.GetColumn(nRow, "FROM_TMS"));
    fce_AddParam("TO_TMS", dsOutVoilAct.GetColumn(nRow, "TO_TMS"));
    fce_AddParam("RACER_NO", dsOutVoilAct.GetColumn(nRow, "RACER_NO"));
    fce_AddParam("QURT_CD", dsOutVoilAct.GetColumn(nRow, "QURT_CD"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
