/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e05040080-service:searchVoilAct" ) {
	    if(dsOutVoilAct.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutVoilAct.GetRowCount());
	    }

	}

	// 저장 후
	if ( sServiceName == "e05040080-service:saveVoilAct" ) {
		if(dsOutVoilAct.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutVoilAct.GetRowCount());
	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	switch(sServiceName) {
		case "common_help-service:searchRacer" :
		case "common_help-service:searchSancBasis" :
			//코드명 조회 후 처리
			fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
			var oFocusComponent = GetNextComponent(true);
			oFocusComponent.setfocus();
			break;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {
	if (obj.id == "edtSANC_BASIS_CD") {
		//변화가 없으면 굳이 실행 하지 않는다.
		if (length(obj.userdata) > 0 && obj.value == obj.userdata ) return;
	} else {
		obj = tbDetail.tb1.edtSANC_BASIS_CD;
	}

	var sInputValue  = trim(obj.value);      //검색 할 자료 

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   

	// 초기화
	sCUROBJ  = tbDetail.tb1.edtSANC_BASIS_CD;
	sGRDTIT  = "";

	switch(obj.id) {
		case "edtSANC_BASIS_CD" :								//양정 코드 
			vREVOBJ[0]   = "tbDetail.tb1.edtSANC_BASIS_CD";		//부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "tbDetail.tb1.edtSB_SMRY";    		//관련 근거를 받을 Text Object명
			sGRDTIT      = "양정코드:관련근거:기간:단위:코드";	//그리드 제목

			sServiceName = "common_help-service:searchSancBasis";
			sInDataSet   = "";								//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutSancBasis";	//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("SANC_BASIS_CD"), Trim(tbDetail.tb1.edtSANC_BASIS_CD.Text));
			break;
	}

	//조회 서비스 실행
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
//row 변경시 
function dsOutVoilAct_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	if (nRow < 0) return;
	fcSearchDetail(obj,nOldRow,nRow);
	fcSearchRacerSanc(obj,nOldRow,nRow);
}

//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutVoilAct_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	//fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutVoilAct.Row = LATEST_ROW_POS;
}
