function fn_SetBkColor_140(FIRST_140, SECOND_140, CURR_140, FIRST_997, SECOND_997, CURR_997)
{
	if (CURR_140 > 0)
	{
		if (FIRST_140 + CURR_140 + SECOND_140 + FIRST_997 + CURR_997 + SECOND_997 >= 2)
		{
			return "lightpink";
		}
		/*if (QURT_CD == "001" && FIRST_140 + CURR_140 + FIRST_997 + CURR_997 >= 2)
		{
			return "lightpink";
		}
		else if (QURT_CD == "002" && SECOND_140 + CURR_140 + SECOND_997 + CURR_997 >= 2)
		{
			return "lightpink";
		}*/
	}
	return "default";
}

function fn_SetBkColor_150(FIRST_150, SECOND_150, CURR_150)
{
	if (CURR_150 > 0)
	{
		if (FIRST_150 + CURR_150 + SECOND_150 >= 2)
		{
			return "lightpink";
		}
	}
	return "default";
}

function fn_SetBkColor_210(QURT_CD, FIRST_210, SECOND_210, CURR_210, FIRST_220, SECOND_220, CURR_220)
{
	if (CURR_210 > 0 || CURR_220 > 0)
	{
		/*
		if (QURT_CD == "001" && FLOOR((FLOOR((FIRST_220  + CURR_220) / 3) + (FIRST_210  + CURR_210)) / 7) > FLOOR((FLOOR(FIRST_220 / 3) + FIRST_210) / 7))
		{
			return "lightpink";
		}
		else if (QURT_CD == "002" && FLOOR((FLOOR((SECOND_220  + CURR_220) / 3) + (SECOND_210  + CURR_210)) / 7) > FLOOR((FLOOR(SECOND_220 / 3) + SECOND_210) / 7))
		{
			return "lightpink";
		} */
		if (FLOOR((FLOOR((FIRST_220  + CURR_220 + SECOND_220) / 3) + (FIRST_210  + CURR_210 + SECOND_210)) / 9) > FLOOR((FLOOR((FIRST_220+SECOND_220) / 3) + FIRST_210 + SECOND_210) / 9))
		{
			return "lightpink";
		}
	}
	return "default";
}

function fn_SetBkColor_997(FIRST_140, SECOND_140, CURR_140, FIRST_997, SECOND_997, CURR_997)
