		{
			return "lightpink";
		}
		else if (QURT_CD == "002" && SECOND_140 + CURR_140 + SECOND_997 + CURR_997 >= 2)
		{
			return "lightpink";
		}*/
	}
	return "default";
}

function cmdConfirm_OnClick(obj)
{
	if (tbDetail.tb1.edtPassword.Text != substr(CURR_DT, 4,4))
	{
		fnc_Message(SNIS_COM_0004, "N");
		return;
	}

	var nRow = dsOutRacerSanc.RowCount();
	var nCurRow;

	dsOutConfirm.Copy(dsOutRacerSanc);
	dsOutConfirm.ClearData();

	for (var i = 0; i < nRow; i++)
	{
		if (dsOutRacerSanc.GetColumn(i, "CONFIRM_YN") == "0")
		{
			nCurRow = dsOutConfirm.AddRow();
			dsOutRacerSanc.CopyToRow(i, dsOutConfirm, nCurRow);
		}
	}

	var nRow = dsOutConfirm.RowCount();
	if (nRow <= 0)
	{
		return;
	}
	fcConfirm();
}

function tbDetail_tb1_edtSANC_BASIS_CD_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == ENTER_KEY) fcCodeNameDisplay(obj);
}

function fcOnChanged(obj,strText)
{
	if (obj.id == "calHOMPAGE_NOTI_SDT" || obj.id == "edtSANC_TERM" || obj.id == "cboUnit") {
		if (!fnc_IsNull(tbDetail.tb1.calHOMPAGE_NOTI_SDT.Value) &&
		    !fnc_IsNull(tbDetail.tb1.edtSANC_TERM.Value) &&
		    !fnc_IsNull(tbDetail.tb1.cboUnit.Value) ) {
				var nDays = 0;
				var sStrDy = tbDetail.tb1.calHOMPAGE_NOTI_SDT.Value;
				var sEndDy = "";
				switch (toNumber(tbDetail.tb1.cboUnit.Value)) {
					case 2 :	//일
						sEndDy = AddDate(sStrDy, toNumber(tbDetail.tb1.edtSANC_TERM.Value) -1);
						break;
					case 3 :	//년
						sEndDy = AddDate(AddMonth(sStrDy, toNumber(tbDetail.tb1.edtSANC_TERM.Value) * 12), -1);
						break;
					case 4 :	//개월
						sEndDy = AddDate(AddMonth(sStrDy, toNumber(tbDetail.tb1.edtSANC_TERM.Value)), -1);
						break;
					default:
				}
				if (sEndDy != "") tbDetail.tb1.calHOMPAGE_NOTI_EDT.Value = sEndDy;
		 }		    
	} else if (obj.id == "calSANC_ACT_DT") {
		tbDetail.tb1.calHOMPAGE_NOTI_SDT.Value = tbDetail.tb1.calSANC_ACT_DT.Value;
	}
}
