function cmdConfirm_OnClick(obj)
{
	if (edtPassword.Text != substr(CURR_DT, 4,4))
	{
		fnc_Message(SNIS_COM_0004, "N");
		return;
	}

	var nRow = dsOutRacerSanc.RowCount();
	var nCurRow;

	dsOutConfirm.Copy(dsOutRacerSanc);
	dsOutConfirm.ClearData();

	for (var i = 0; i < nRow; i++)
	{
		if (dsOutRacerSanc.GetColumn(i, "CONFIRM_YN") == "0")
		{
			nCurRow = dsOutConfirm.AddRow();
			dsOutRacerSanc.CopyToRow(i, dsOutConfirm, nCurRow);
		}
	}

	var nRow = dsOutConfirm.RowCount();
	if (nRow <= 0)
	{
		return;
	}
	fcConfirm();
}

// 출전정지 숫자를 구함.
function fcSuspdTmsCountCalc() {
	if(dsOutLowRecord.rowcount > 0) {
		var prevRank = "0";
		var prevRacerNo = "";
		var prevSeq = 1;
		var curRank = "";
		var curRacerNo = "";
		var curSeq = 1;
		var suspdTms = 0;
		for(var i = 0; i < dsOutLowRecord.TotalRowCount(); i++) {
			curRacerNo = dsOutLowRecord.GetColumn(i,"RACER_NO");
			curRank = dsOutLowRecord.GetColumn(i,"RANK");

			// 이전 등수와 현 등수가 동일하면 seq를 1씩 증가, 아니면 Seq를 1로 설정
			if( curRacerNo == prevRacerNo && prevRank == curRank) {
				curSeq = prevSeq + 1;
			}
			else {
				curSeq = 1;
			}
			dsOutLowRecord.SetColumn(i,"SEQ",curSeq);

			// seq를 기준값으로 나눈 나머지가 0이면 1회차 출전정지, 현재 등수가 6인경우
			// 6착이 기준값만큼 연속 발생했음을 의미함.
			if(curSeq % parseInt(edBase.Text) == 0 && curRank == '6') {
				suspdTms = 1;
			}
			else{
				suspdTms = 0;
			}
			// 출전정지 값을 Setting
			dsOutLowRecord.SetColumn(i,"DIV",suspdTms);
			prevRacerNo = curRacerNo;
			prevRank = curRank;
			prevSeq = curSeq;
		}
	}

	// 이번회차에서 출전정지 수를 합하여 출전정지 표시
	var racerNo = "";
	for(var i=0; i < dsOutRaceResult.TotalRowCount(); i++) {
		racerNo = dsOutRaceResult.GetColumn(i,"RACER_NO");
		// 당 회차 출전정지(DIV=1인 데이터) 숫자를 출전전수 회수로 지정.
		dsOutLowRecord.Filter("DIV='1'&&TMS='"+cboTms.Value+"'&&RACER_NO='"+racerNo+"'");
		dsOutRaceResult.setColumn(i,"SUSPD_TMS", dsOutLowRecord.rowcount);
	}
	grdPageList.SetFocus();
}
