/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(cboMbrCd, "경정장")) return false;
   if (!fnc_IsMandatory(cboTms, "회차")) return false;
   if (!fnc_IsMandatory(meBase, "기준값")) return false;
}

//조회 
function fcSearch(obj) {
	if(fcSearchCheck() == false) return;

    var sServiceName = "e05040080-service:searchRacerDelayStart";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDelayStart=dsOutDelayStart";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTms.Value);
    fce_AddParam("BAS_VALUE", meBase.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function fcSearchRacerSanc(obj,nOldRow,nRow) {

    var sServiceName = "e05040080-service:searchRacerSancDelayStart";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutRacerSanc=dsOutRacerSanc";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTms.Value);
    fce_AddParam("BAS_VALUE", meBase.Value);
    fce_AddParam("RACER_NO", obj.GetColumn(nRow, "RACER_NO"));

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutRacerSanc))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutRacerSanc;
	sMandatoryColID   = "CHK";
	sMandatoryColName = "선택";
	oGrid             = grdPageListSanc;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "e05040080-service:saveRacerSanc";
    var sInDataSet   = "dsOutRacerSanc=dsOutRacerSanc:u";
    var sOutDataSet  = "dsOutRacerSanc=dsOutRacerSanc";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function fcConfirmCheck(obj) {
	// 필수항목체크
	// 필수항목체크
	oDataSet          = dsOutConfirm;
	sMandatoryColID   = "RACER_NO|SANC_BASIS_CD|SANC_TERM|SMRY|SANC_RSN_CD|SANC_RSN|SANC_ACT_DT|HOMEPAGE_NOTI_SDT|HOMEPAGE_NOTI_EDT";
	sMandatoryColName = "등록번호|양정코드|제재처분기간|관련근거|사유|사유내역|처분일자|공지시작일|공지종료일";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;
}

function fcConfirm(obj) {
    if ( !fcConfirmCheck() ) return;
	var sServiceName = "e05040080-service:saveConfirm";
    var sInDataSet   = "dsOutRacerSanc=dsOutConfirm:u";
    var sOutDataSet  = "dsOutRacerSanc=dsOutConfirm";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}
