/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e05040080-service:searchRacerDelayStart" ) {
	    if(dsOutDelayStart.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutDelayStart.GetRowCount());
	    }
	}

	// 저장 후
	if ( sServiceName == "e05040080-service:saveVoilAct" ) {
		if(dsOutDelayStart.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutDelayStart.GetRowCount());
	    }
	}

	// 회차 목록 생성후
	if( sServiceName == "common_race-service:searchRaceTms") {
		cboTms.Index = 0;
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 Post Process!
+-------------------------------------------------------------------------------------------------*/
function fcPostProcess(sServiceName) {
	switch(sServiceName) {
		case "common_help-service:searchRacer" :
		case "common_help-service:searchSancBasis" :
			//코드명 조회 후 처리
			fce_MultiRowDisplay(this, vREVOBJ, sGRDTIT, sCUROBJ);
			var oFocusComponent = GetNextComponent(true);
			oFocusComponent.setfocus();
			break;
	}
}
/*-------------------------------------------------------------------------------------------------+
>>  코드나 명 입력 후 Lost Focus 시 해당 정보 Display
+-------------------------------------------------------------------------------------------------*/
function fcCodeNameDisplay(obj) {
	//변화가 없으면 굳이 실행 하지 않는다.
	if (length(obj.userdata) > 0 && obj.value == obj.userdata ) return;

	var sInputValue  = trim(obj.value);      //검색 할 자료 

	var sServiceName = "";                   //서버에서 호출 될 Service 명
	var sActionName  = "";                   //서버에서 호출 될 Action 명
	var sInDataSet   = "";                   //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet  = "";                   //조회 후 돌려 받을 DataSet 명칭   

	// 초기화
	sCUROBJ  = edtSANC_BASIS_CD;
	sGRDTIT  = "";

	switch(obj.id) {
		case "edtSANC_BASIS_CD" :								//양정 코드 
			vREVOBJ[0]   = "edtSANC_BASIS_CD";		//부서 코드를 받을 Text Object명
			vREVOBJ[1]   = "edtSB_SMRY";    		//관련 근거를 받을 Text Object명
			sGRDTIT      = "양정코드:관련근거:기간:단위:코드";	//그리드 제목

			sServiceName = "common_help-service:searchSancBasis";
			sInDataSet   = "";								//자료 저장 시 보낼 DataSet 명칭
			sOutDataSet  = "gdsSelectItem=dsOutSancBasis";	//조회 후 돌려 받을 DataSet 명칭  

			fce_AddParam(Trim("SANC_BASIS_CD"), Trim(edtSANC_BASIS_CD.Text));
			break;
	}

	//조회 서비스 실행
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//row 변경시 
function dsOutDelayStart_OnRowPosChanged(obj,nOldRow,nRow)
{
	//LATEST_ROW_POS = nOldRow;
	if (nRow < 0) return;
	if(nRow >= 0) {
		dsOutRacerSanc.ClearData();
		fcSearchRacerSanc(obj,nOldRow,nRow);
	}
}

//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutDelayStart_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	//if(LATEST_ROW_POS > 0) dsOutDelayStart.Row = LATEST_ROW_POS;
	dsOutDelayStart_OnRowPosChanged();
}

//기준년도 변경시 
function cboStndYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//경정장 코드 변경시 
function cboMbrCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

//경정장 DataSet Load 완료 시
function dsOutMbrCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	cboMbrCd.Value = CURR_USER_MBR_CD;
	fce_GetRaceTms(cboStndYear.Value, cboMbrCd.Value, null, "dsOutTms");
}

function cmdConfirm_OnClick(obj)
{
	if (edtPassword.Text != substr(CURR_DT, 4,4))
	{
		fnc_Message(SNIS_COM_0004, "N");
		return;
	}

	var nRow = dsOutRacerSanc.RowCount();
	var nCurRow;

	dsOutConfirm.Copy(dsOutRacerSanc);
	dsOutConfirm.ClearData();

	for (var i = 0; i < nRow; i++)
	{
		if (dsOutRacerSanc.GetColumn(i, "CONFIRM_YN") == "0")
		{
			nCurRow = dsOutConfirm.AddRow();
			dsOutRacerSanc.CopyToRow(i, dsOutConfirm, nCurRow);
		}
	}

	var nRow = dsOutConfirm.RowCount();
	if (nRow <= 0)
	{
		return;
	}
	fcConfirm();
}
