function fcInit(obj) {	
    dsOutDoping.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
   if (!fnc_IsMandatory(cboStndYear, "년도")) return false;
   if (!fnc_IsMandatory(cboMbrCd, "경정장")) return false;
   if (!fnc_IsMandatory(cboTMS, "회차")) return false;
}

//조회 
function fcSearch(obj) {
	if(fcSearchCheck() == false) return;

    var sServiceName = "e05050010-service:searchDoping";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutDoping=dsOutDoping";

    fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTMS.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutDoping))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutDoping;
	sMandatoryColID   = "CHK";
	sMandatoryColName = "선택";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "e05050010-service:saveDoping";
    var sInDataSet   = "dsOutDoping=dsOutDoping:u";
    var sOutDataSet  = "dsOutDoping=dsOutDoping";

	fce_AddParam("STND_YEAR", cboStndYear.Value);
    fce_AddParam("MBR_CD", cboMbrCd.Value);
    fce_AddParam("TMS", cboTMS.Value);
	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

