/*-------------------------------------------------------------------------------------------------+
>>  인쇄 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {
	var nRow = dsOutDoping.GetRowCount();
	var nAddRow;
	var nAddRow_Cnt;
	var nSeq = 1;
	var nFindRow;
	var nRACER_PERIO_NO;

	dsOutDoping_Report.ClearData();
	dsOutCnt.ClearData();
	for (var i = 0; i < nRow; i++)
	{
		if (dsOutDoping.GetRowType(i) == "normal" && dsOutDoping.GetColumn(i, "CHK") == "1")
		{
			nAddRow = dsOutDoping_Report.AddRow();
			dsOutDoping.CopyToRow(i, dsOutDoping_Report, nAddRow);
			dsOutDoping_Report.SetColumn(nAddRow, "SEQ", nSeq);
			nSeq += 1;
			// 검사인원
			nRACER_PERIO_NO = dsOutDoping.GetColumn(i, "RACER_PERIO_NO");
			nFindRow = dsOutCnt.FindRow("RACER_PERIO_NO", nRACER_PERIO_NO);
			if (nFindRow == -1)
			{
				nAddRow_Cnt = dsOutCnt.AddRow();
				dsOutCnt.SetColumn(nAddRow_Cnt, "RACER_PERIO_NO", nRACER_PERIO_NO);
				dsOutCnt.SetColumn(nAddRow_Cnt, "CNT", 1);
			}
			else
			{
				dsOutCnt.SetColumn(nFindRow, "CNT", dsOutCnt.GetColumn(nFindRow, "CNT") + 1);
			}
		}
	}

	if (dsOutDoping_Report.GetRowCount() == 0)
	{
		fnc_Message(SNIS_BOA_E005, "N");
		return;
	}
	var logImgUrl = GBL_REPORT_URL+"log.jpg";
	var sTMS	  = dsOutDoping_Report.GetColumn(0, "STND_YEAR") + "년도@@@제@";
		sTMS	 += Lpad(dsOutDoping_Report.GetColumn(0, "TMS"), "0", 2) + "@회차";
	var sCNT;

	var nRow = dsOutCnt.GetRowCount();
	for (var i = 0; i < nRow; i++)
	{
		sCNT	 += dsOutCnt.GetColumn(i, "RACER_PERIO_NO") + "기-";
		sCNT	 += ToString(dsOutCnt.GetColumn(i, "CNT")) + "명";
		if (i < nRow - 1)
		{
			sCNT	 += "@";
		}
	}

	sCNT = dsOutDoping_Report.GetRowCount() + "명@(" + sCNT + ")";

	var sINS_TIME = cboInsTime.Value;
	var sSubUrl   = "";                     			//레포트 파일이 있는 서브디렉토리
	var sRptNm    = "e05059010_doping_test.jrf";       	//레포트 파일명
	var sDataSet  = "dsOutDoping=dsOutDoping_Report";   //"UBI 쿼리명, MIP Dataset명"
	var sArg      = "IMG_URL="+logImgUrl;
		sArg 	 += " TMS="+sTMS;
		sArg 	 += " CNT="+sCNT;
		sArg 	 += " INS_TIME="+sINS_TIME;

	var sRptDrct  = 1;                               	//출력방향(0 : 수직, 1 : 수평)

	fnc_RPTPrint(sSubUrl, sRptNm, sDataSet, sArg, sRptDrct);
}
