//조회 
function fcSearch(obj) {
	fcSearchCheck();

    var sServiceName = "e05060010-service:searchAcctTrans";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutAcctTrans=dsOutAcctTrans dsOutAcctTransDetail=dsOutAcctTransDetail";

    fce_AddParam("FROM_DATE", calFROM_DATE.Value);
    fce_AddParam("TO_DATE", calTO_DATE.Value);
    fce_AddParam("MJR_NM", edtMJR_NM.Text);
    fce_AddParam("MJR_BK_ACT", edtMJR_BK_ACT.Text);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

