/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	if (fnc_DatasetChangeCheck(dsOutAcctTrans))
	{
		// 필수항목체크
		oDataSet          = dsOutAcctTrans;
		sMandatoryColID   = "MJR_BK_ACT|BK_NM";
		sMandatoryColName = "계좌번호|은행명";
		oGrid             = grdPageList;
		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}

	var strMJR_BK_ACT = dsOutAcctTrans.GetColumn(dsOutAcctTrans.row, "MJR_BK_ACT");
	var nRow = dsOutAcctTransDetail.RowCount();

	if (fnc_DatasetChangeCheck(dsOutAcctTransDetail))
	{
		for (var i = 0; i < nRow; i++)
		{
			if (dsOutAcctTransDetail.GetRowType(i) == "insert")
			{
				dsOutAcctTransDetail.SetColumn(i, "MJR_BK_ACT", strMJR_BK_ACT);
			}
		}

		// 필수항목체크
		oDataSet          = dsOutAcctTransDetail;
		sMandatoryColID   = "NM|TR_DT|MJR_BK_ACT|TR_AMT|JEOGYO";
		sMandatoryColName = "성명|거래일자|주거래계좌번호|거래금액|적요";
		oGrid             = grdPageListDetail;

		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}
	}
	return true;
}

function fcSaveCheckDetail(obj) {


}

function fcSave(obj) {
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutAcctTrans) && !fnc_DatasetChangeCheck(dsOutAcctTransDetail))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;	
	}

    if (!fcSaveCheck())	return;
    if (fnc_DatasetChangeCheck(dsOutAcctTrans))
    {
		var sServiceName = "e05060010-service:saveAcctTrans";
		var sInDataSet   = "dsOutAcctTrans=dsOutAcctTrans:u";
		var sOutDataSet  = "dsOutAcctTrans=dsOutAcctTrans";

		fce_AddParam("FROM_DATE", calFROM_DATE.Value);
		fce_AddParam("TO_DATE", calTO_DATE.Value);
		fce_AddParam("MJR_NM", edtMJR_NM.Text);
		fce_AddParam("MJR_BK_ACT", edtMJR_BK_ACT.Text);

		fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
	}

	fcSaveDetail();
	return;
}

function fcSaveDetail(obj) {

	if (!fnc_DatasetChangeCheck(dsOutAcctTransDetail)) return;
	var sServiceName = "e05060010-service:saveAcctTransDetail";
    var sInDataSet   = "dsOutAcctTransDetail=dsOutAcctTransDetail:u";
    var sOutDataSet  = "dsOutAcctTransDetail=dsOutAcctTransDetail";

	var strMJR_BK_ACT = dsOutAcctTrans.GetColumn(dsOutAcctTrans.row, "MJR_BK_ACT");

	fce_AddParam("FROM_DATE", calFROM_DATE.Value);
    fce_AddParam("TO_DATE", calTO_DATE.Value);
    fce_AddParam("MJR_NM", edtMJR_NM.Value);
    fce_AddParam("MJR_BK_ACT", strMJR_BK_ACT);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

