/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	switch(obj.GetForm().id) {
	case "divMain" :
		if (dsOutAcctTrans.ColCount() > 0) {
			var nRow = dsOutAcctTrans.AddRow();        	  	//Record Add
		} else {
			fnc_Message(SNIS_COM_0002, "N");      			//DataSet의 Header가 없어서 오류
		}
		break;

	case "divDetail" :
		if (length(dsOutAcctTrans.GetColumn(dsOutAcctTrans.row, "MJR_BK_ACT")) = 0)
		{
			fnc_Message(SNIS_BOA_E004, "N");
			return;
		}
		if (dsOutAcctTransDetail.ColCount() > 0) {
			var nRow = dsOutAcctTransDetail.AddRow();		//Record Add
			dsOutAcctTransDetail.SetColumn(nRow,"MJR_BK_ACT",dsOutAcctTrans.getColumn(dsOutAcctTrans.row,"MJR_BK_ACT"));
		} else {
			fnc_Message(SNIS_COM_0002, "N");      			//DataSet의 Header가 없어서 오류
		}
		break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	switch(obj.GetForm().id) {
	case "divMain" :
		var filterString = "";
		for(var i = 0; i < dsOutAcctTrans.rowcount; i++)
		{
			if(dsOutAcctTrans.getColumn(i,"CHK") == "1")
			{
				if(filterString == "")
				{
					filterString = "MJR_BK_ACT = '" + dsOutAcctTrans.getColumn(i,"MJR_BK_ACT") + "'";
				}
				else
				{
					filterString = filterString + "||MJR_BK_ACT = '" + dsOutAcctTrans.getColumn(i,"MJR_BK_ACT") + "'";
				}
			}
		}
		if (fnc_DeleteCheck(dsOutAcctTrans, "CHK", "1"))
		{
			dsOutAcctTransDetail.Filter(filterString);
			for(var i = dsOutAcctTransDetail.rowcount -1; i >= 0; i--)
			{
				dsOutAcctTransDetail.DeleteRow(i);
			}
			//return;
		}
		dsOutAcctTransDetail.Filter("MJR_BK_ACT='"+ dsOutAcctTrans.GetColumn(dsOutAcctTrans.row,"MJR_BK_ACT") +"'");
		break;

	case "divDetail" :
		//DataSet의 Header 정보가 있을 때  Add 처리 	
		if ( !fnc_DeleteCheck(dsOutAcctTransDetail, "CHK", "1") ) return;
		//dsOutAcctTransDetail.SetFocus();                                  //Record 삭제 후 Grid로 Focus
		break;
	}
}

