/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_Result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e05060010-service:searchAcctTrans" ) {
	    if(dsOutAcctTrans.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutAcctTrans.GetRowCount());
			dsOutAcctTransDetail.Filter("MJR_BK_ACT='"+ dsOutAcctTrans.GetColumn(dsOutAcctTrans.row,"MJR_BK_ACT") +"'");
	    }
	    return;
	}

	// 저장 후
	if ( sServiceName == "e05060010-service:saveAcctTrans" ) {
		if(dsOutAcctTrans.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutAcctTrans.GetRowCount());
	    }
	    return;
	}

	if ( sServiceName == "e05060010-service:saveAcctTransDetail") {
		fcSearch();
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

