//row 변경시 
function dsOutAcctTrans_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
	//fcSearchDetail(obj.GetColumn(nRow, "MJR_BK_ACT"));
	dsOutAcctTransDetail.Filter("MJR_BK_ACT='" + dsOutAcctTrans.GetColumn(nRow,"MJR_BK_ACT") + "'");
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutAcctTrans_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	//if(LATEST_ROW_POS > 0) dsOutAcctTrans.Row = LATEST_ROW_POS;
}


function dsOutAcctTransDetail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var strGetDay;
	if (strColumnID == "TR_DT")
	{
		switch (GetDay(varNewValue))
		{
			case "0":
				strGetDay = "일";
				break;
			case "1":
				strGetDay = "월";
				break;
			case "2":
				strGetDay = "화";
				break;
			case "3":
				strGetDay = "수";
				break;
			case "4":
				strGetDay = "목";
				break;
			case "5":
				strGetDay = "금";
				break;
			case "6":
				strGetDay = "토";
				break;
		}
		dsOutAcctTransDetail.SetColumn(nRow, "ADAY", strGetDay);
	}
}
