/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutAcctUpload))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	var nRow = dsOutAcctUpload.RowCount();
	var TR_AMT_IN;
	var TR_AMT_OUT;
	var TR_TIME;

	for (var i = 0; i < nRow; i++)
	{
		TR_TIME = dsOutAcctUpload.GetColumn(i, "TR_TIME");
		if (length(TR_TIME) > 0 && (IsDigit(TR_TIME) == false || ToNumber(Left(TR_TIME, 2)) > 24 || ToNumber(Left(TR_TIME, 2)) < 0 || ToNumber(Right(TR_TIME, 2)) < 0 || ToNumber(Right(TR_TIME, 2)) > 59)) 
		{
			fnc_Message(SNIS_BOA_E003, "N");
			return false;
		}
		if (GetDay(dsOutAcctUpload.GetColumn(i, "TR_DT")) == -1) 
		{
			fnc_Message(SNIS_BOA_E003, "N");
			return false;
		}
		if (IsDigit(dsOutAcctUpload.GetColumn(i, "MJR_BK_ACT")) = 0)
		{
			fnc_Message(SNIS_BOA_E003, "N");
			return false;
		}
		if (IsDigit(dsOutAcctUpload.GetColumn(i, "BK_ACT")) = 0)
		{
			fnc_Message(SNIS_BOA_E003, "N");
			return false;
		}
		TR_AMT_IN = dsOutAcctUpload.GetColumn(i, "TR_AMT_IN");
		if (length(TR_AMT_IN) > 0 && IsDigit(TR_AMT_IN) = 0)
		{
			fnc_Message(SNIS_BOA_E003, "N");
			return false;
		}
		TR_AMT_OUT = dsOutAcctUpload.GetColumn(i, "TR_AMT_OUT");
		if (length(TR_AMT_OUT) > 0 && IsDigit(TR_AMT_OUT) = 0)
		{
			fnc_Message(SNIS_BOA_E003, "N");
			return false;
		}
	}

	// 필수항목체크
	oDataSet          = dsOutAcctUpload;
	sMandatoryColID   = "NM|TR_DT|MJR_BK_ACT|BK_NM|BK_ACT|TR_AMT";
	sMandatoryColName = "성명|거래일자|주거래계좌번호|주거래계좌은행|본인계좌번호|거래금액";
	oGrid             = grdPageList;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "e05060020-service:saveAcctUpload";
    var sInDataSet   = "dsOutAcctUpload=dsOutAcctUpload:u";
    var sOutDataSet  = "dsOutAcctUpload=dsOutAcctUpload";

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

