/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 저장 후
	if ( sServiceName == "e05060020-service:saveAcctUpload" ) {
		if(dsOutAcctUpload.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutAcctUpload.GetRowCount());
	    }
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

