//row 변경시 
function dsOutAcctUpload_OnRowPosChanged(obj,nOldRow,nRow)
{
	LATEST_ROW_POS = nOldRow;
}


//DataSet 정상  로드시  (저장후 조회시 저장시점으 Rowposition유지)
function dsOutAcctUpload_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	fnc_ProgressClose(); //팝업창이 Close안되었으면 Close시킨다.
	if(LATEST_ROW_POS > 0) dsOutAcctUpload.Row = LATEST_ROW_POS;
}

function cmdExcelUpload_OnClick(obj)
{
	var strFileName;
	var nRow;
	var sMJR_NM;
	var sBK_NM;
	var sMJR_BK_ACT;
	var sColumnInfo;
	var TR_AMT_IN;
	var TR_AMT_OUT;
	var nDsModRow	= 2;
	var nExModRow	= 2;

	FileDialog.Type = "OPEN";
	FileDialog.Filter = "Excel(.xls)|*.xls|";

	if (!FileDialog.open()){
		return;
	}
	dsOutSheet.ClearData();
	dsOutAcctUpload.ClearData();
	strFileName = FileDialog.FilePath + "\\" + FileDialog.FileName;
	ext_ExcelImportByIndex(strFileName, 0, dsOutSheet.ID);
	nRow = dsOutSheet.RowCount();
	//주거래계좌-성명
	sMJR_NM = dsOutSheet.GetColumn(0, "Col01");
	//주거래계좌-은행
	sBK_NM = dsOutSheet.GetColumn(0, "Col02");
	//주거래계좌-계좌번호
	sMJR_BK_ACT = Replace(dsOutSheet.GetColumn(0, "Col03"), "-", "");
	stTitle.Text = sMJR_NM + " 계좌 입,출금내역 " + sBK_NM + " 은행 " + sMJR_BK_ACT;
	// 엑셀컬럼에 관한 dataset컬럼
	sColumnInfo	 = "ADAY=Col01, TR_DT=Col02, TR_TIME=Col03, JEOGYO=Col04, NM=Col05, ";
	sColumnInfo += "TR_AMT_IN=Col06, TR_AMT_OUT=Col07, AGENCY=Col08, TERMINAL=Col09, BK_ACT=Col10";

	for (var i = nExModRow; i < nrow; i++)
	{
		dsOutAcctUpload.AddRow();
		dsOutSheet.CopyToRow(i, dsOutAcctUpload, i - nDsModRow, sColumnInfo);
		//엑셀행
		dsOutAcctUpload.SetColumn(i - nDsModRow, "SEQ", i + 1);
		//거래일자
		dsOutAcctUpload.SetColumn(i - nDsModRow, "TR_DT", Replace(dsOutAcctUpload.GetColumn(i - nDsModRow, "TR_DT"), "-", ""));
		//요일
		dsOutAcctUpload.SetColumn(i - nDsModRow, "ADAY", fcGetAday(dsOutAcctUpload.GetColumn(i - nDsModRow, "TR_DT")));
		//거래시간
		dsOutAcctUpload.SetColumn(i - nDsModRow, "TR_TIME", Replace(dsOutAcctUpload.GetColumn(i - nDsModRow, "TR_TIME"), ":", ""));
		//주거래계좌-성명
		dsOutAcctUpload.SetColumn(i - nDsModRow, "MJR_NM", sMJR_NM);
		//주거래계좌-은행
		dsOutAcctUpload.SetColumn(i - nDsModRow, "BK_NM", sBK_NM);
		//주거래계좌-계좌번호
		dsOutAcctUpload.SetColumn(i - nDsModRow, "MJR_BK_ACT", sMJR_BK_ACT);
		//본인계좌번호
		dsOutAcctUpload.SetColumn(i - nDsModRow, "BK_ACT", Replace(dsOutAcctUpload.GetColumn(i - nDsModRow, "BK_ACT"), "-", ""));
		//입금
		TR_AMT_IN = dsOutAcctUpload.GetColumn(i - nDsModRow, "TR_AMT_IN");
		if (ToNumber(TR_AMT_IN) > 0)
		{
			dsOutAcctUpload.SetColumn(i - nDsModRow, "IO_GUBUN", "1");
			dsOutAcctUpload.SetColumn(i - nDsModRow, "TR_AMT", TR_AMT_IN);
		}
		//출금
		TR_AMT_OUT = dsOutAcctUpload.GetColumn(i - nDsModRow, "TR_AMT_OUT");
		if (ToNumber(TR_AMT_OUT) > 0)
		{
			dsOutAcctUpload.SetColumn(i - nDsModRow, "IO_GUBUN", "2");
			dsOutAcctUpload.SetColumn(i - nDsModRow, "TR_AMT", TR_AMT_OUT);
		}
	}
}

function fcGetAday(varNewValue)
{
	var strGetDay;
	switch (GetDay(varNewValue))
	{
		case "0":
			strGetDay = "일";
			break;
		case "1":
			strGetDay = "월";
			break;
		case "2":
			strGetDay = "화";
			break;
		case "3":
			strGetDay = "수";
			break;
		case "4":
			strGetDay = "목";
			break;
		case "5":
			strGetDay = "금";
			break;
		case "6":
			strGetDay = "토";
			break;
	}

	return strGetDay;
}
