+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
    return true;
}

//조회 
function fcSearch(obj) {
	//fcSearchCheck();

    var sServiceName = "e05060060-service:searchPrivitWork";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutPrivitWork=dsOutPrivitWork";

    fce_AddParam("FROM_DATE", calFROM_DATE.Value);
    fce_AddParam("TO_DATE", calTO_DATE.Value);
    fce_AddParam("ACT_DESC", edtACT_DESC.Value);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

//첨부파일 조회 
function fcFileSearch(obj,nOldRow,nRow) {

    var sServiceName = "e05060060-service:searchUploadFile";
    var sInDataSet   = "";
    var sOutDataSet  = "dsOutUploadFile=dsOutUploadFile";

    var sACT_DT = obj.GetColumn(nRow, "ACT_DT");
    var sACT_NM = obj.GetColumn(nRow, "ACT_NM");

    fce_AddParam("ACT_DT", sACT_DT);
    fce_AddParam("ACT_NM", sACT_NM);

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsOutPrivitWork) && !fnc_DatasetChangeCheck(dsOutUploadFile))
	{
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}

	// 필수항목체크
	oDataSet          = dsOutPrivitWork;
	sMandatoryColID   = "ACT_NM|ACT_DT";
	sMandatoryColName = "장소|일자";
	oGrid             = grdPageList;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}
	return true;
}

function fcSave(obj) {
    if ( !fcSaveCheck() ) return;
	var sServiceName = "e05060060-service:savePrivitWork";
    var sInDataSet   = "dsOutPrivitWork=dsOutPrivitWork:u ";
		sInDataSet  += "dsOutUploadFile=dsOutUploadFile:u ";
    var sOutDataSet  = "dsOutPrivitWork=dsOutPrivitWork";

	fce_AddParam("FROM_DATE", calFROM_DATE.Value);
    fce_AddParam("TO_DATE", calTO_DATE.Value);
    fce_AddParam("ACT_NM", edtACT_NM.Value);

    if (fcFileUpload() == false) return;

	fce_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack");
}

function fcFileUpload()
{
	var nRowCount = dsOutUploadFile.GetRowCount();
	var nRow;
	if( nRowCount <= 0 ) return;

	nRow = dsOutPrivitWork.currow;
	var sACT_DT = dsOutPrivitWork.GetColumn(nRow, "ACT_DT");
    var sACT_NM = dsOutPrivitWork.GetColumn(nRow, "ACT_NM");

	for ( var i = 0; i < nRowCount; i++ ) {
		if(dsOutUploadFile.GetRowType(i) == "insert" && dsOutUploadFile.GetColumn(i, "CHK") == "1")
		{
			var rtn_arr = fnc_FileWrite(  divBtnLst.HttpFile 
										, dsOutUploadFile.GetColumnNF(i, "FILE_URL" )
										, dsOutUploadFile.GetColumnNF(i, "FILE_URL" ) );

			if ( rtn_arr[0] != "SUCC" ) 
			{
				alert("파일 업로드 에러발생");
				return false;
			}

			dsOutUploadFile.SetColumn(i, "FILE_URL"    , rtn_arr[1] );
			dsOutUploadFile.SetColumn(i, "ACT_DT", sACT_DT);
			dsOutUploadFile.SetColumn(i, "ACT_NM", sACT_NM);
		}
	}
}

