/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	if (dsOutPrivitWork.ColCount() > 0) 
	{
		dsOutPrivitWork.AddRow();			//Record Add
		edACT_NM.SetFocus();          		//입력 첫 항목으로 Focus
	} 
	else 
	{
		fnc_Message(SNIS_COM_0002, "N");	//DataSet의 Header가 없어서 오류
	}
}
function fcFileAddRow(obj) {
	var nRow 	= dsOutPrivitWork.currow;
	var sACT_DT = dsOutPrivitWork.GetColumn(nRow, "ACT_DT");
	var sACT_NM = dsOutPrivitWork.GetColumn(nRow, "ACT_NM");

	if (length(sACT_DT) <= 0 || length(sACT_NM) <= 0)
	{
		fnc_Message("활동내역을 선택하여 주십시요.", "N");
		return false;
	}

    var sResponse = fce_GetSelectFile();

    if (!fnc_IsNull(sResponse)) 
    {
		var row 	= dsOutUploadFile.AddRow();

        dsOutUploadFile.SetColumn(row, "CHK", "1");
        dsOutUploadFile.SetColumn(row, "FILE_URL", sResponse[0] + "\\" + sResponse[1]);
        dsOutUploadFile.SetColumn(row, "FILE_NM", sResponse[1]);
    }
}
/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	//첨부파일 삭제
	var nRowCount = dsOutUploadFile.RowCount();
	for (var nRow = 0; i < nRowCount; i++)
	{
		dsOutUploadFile.SetColumn(nRow, "CHK", "1");
	}
	fcFileDelRow();
	//한줄씩 삭제(첨부파일 삭제해야 하기때문에 한줄씩 삭제 해야 한다.)
	dsOutPrivitWork.DeleteRow(dsOutPrivitWork.currow);
	grdPageList.SetFocus();                                  //Record 삭제 후 Grid로 Focus

}
function fcFileDelRow(obj) {
	//파일삭제
	var splitArr;
	var lenSplitArr;
	var nRowCount = dsOutUploadFile.RowCount();

	dsOutUploadFile.FireEvent = false;
	for ( var i = nRowCount; i >= 0; i-- ) 
	{
		if (dsOutUploadFile.GetColumn(i, "CHK") == 1) 
		{
			if (dsOutUploadFile.GetRowType(i) != "insert")
			{
				splitArr    = (dsOutUploadFile.GetColumn(i, "FILE_URL")).split("\\");
				lenSplitArr = toInteger(length(splitArr)) - toInteger(1);
//				fnc_FileDelete(splitArr[lenSplitArr]);
			}

			dsOutUploadFile.DeleteRow(i);
		}
	}
	dsOutUploadFile.FireEvent = true;
}
