/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// 조회 후
	if ( sServiceName == "e05060060-service:searchPrivitWork" ) {
	    if(dsOutPrivitWork.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutPrivitWork.GetRowCount());
	    }
	}

	// 저장 후
	if ( sServiceName == "e05060060-service:savePrivitWork" ) {
		if(dsOutPrivitWork.GetRowCount() > 0){
			fnc_SetStatus(SNIS_BOA_0003, dsOutPrivitWork.GetRowCount());
	    }
	    fcFileSearch(dsOutPrivitWork, LATEST_ROW_POS, LATEST_ROW_POS);
	}

	//오류가 없이 정상 처리되었다면...
	fcPostProcess(sServiceName);
}

